insert  into `mysiteapp_loftk_articles`(`id`,`newtitle`,`newcontent`,`author`,`newtype`,`newimg`,`count`,`newremark`,`fstcreate`,`lastmodify`,`isdel`) values (1474,'特种部队2:复仇','<p>\r\n	<span style=\"font-family:tahoma, arial, 宋体;font-size:14px;line-height:22px;background-color:#F7F7F7;\">《特种部队2：正面对决》（英语：G.I. Joe 2: Retaliation），2013年上映的美国科幻动作电影，是2009年《特種部隊:眼鏡蛇的崛起》的续集作品。本片根据孩之宝玩具公司特种部队系列改编，由巨石强森、布鲁斯威利、查宁·塔图姆、雷·帕克和李秉宪主演。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:tahoma, arial, 宋体;font-size:14px;line-height:22px;background-color:#F7F7F7;\">\r\n	<p style=\"font-family:tahoma, arial, 宋体;font-size:14px;background-color:#FFFFFF;\">\r\n		恐怖组织“眼镜蛇”（Cobra）企图征服世界，美国政府为了保卫国家而组成了特种部队。杜克领导特种部队，为了抵御邪恶的眼镜蛇头领及其部下，发誓不论何时何地要为自由而战，赴汤蹈火，除暴安良以伸张正义。战斗不断升级，对决一触即发……\r\n	</p>\r\n	<p style=\"font-family:tahoma, arial, 宋体;font-size:14px;background-color:#FFFFFF;\">\r\n		　　最近，地球上出现了一个极其神秘的恐怖组织，它的名字叫“眼镜蛇”，而让“眼镜蛇”闻名全球的原因，则是源自其对埃菲尔铁塔的一次恐怖行动，在“眼镜蛇”的恐怖轰炸之下，这座法国的<a target=\"_blank\" href=\"http://baike.so.com/doc/2641261.html\">标志性建筑</a>物顷刻之间化为乌有。很显然，“眼镜蛇”的这次行动只是一个开始，它向全世界宣告“眼镜蛇”的成立，而他们的目的则隐藏得更深，许多国家开始感到不安，因为他们不知道什么时候“眼镜蛇”的恐怖行径就会落到自己的国家当中，世界陷入了恐慌状态。\r\n	</p>\r\n	<p style=\"font-family:tahoma, arial, 宋体;font-size:14px;background-color:#FFFFFF;\">\r\n		　　<a href=\"http://p12.qhimg.com/t01d2e4cb672ca833fa.jpg\"><img class=\"show_img layoutright\" id=\"img_4793857\" title=\"a6efce1b9d16fdfa2b38f84bb48f8c5494ee7b3a.jpg\" alt=\"a6efce1b9d16fdfa2b38f84bb48f8c5494ee7b3a.jpg\" src=\"http://p12.qhimg.com/dr/200_200_/t01d2e4cb672ca833fa.jpg\" style=\"border:1px solid #D9D9D9;\" /></a>北约对此也感到大为棘手，因此这时候，总部设在<a target=\"_blank\" href=\"http://baike.so.com/doc/96810.html\">布鲁塞尔</a>的一支秘密行动小组就开始发威了他的威力。这支名为G.I. JOE的特种部队由霍克将军（丹尼斯·奎德饰）担任领头羊，手下高手云集，副指挥官是公爵（查宁·塔图姆饰），参加过越南的他曾经与霍克一起多次出生入死；红发女（蕾切尔·尼科尔斯饰）是一个武术高手，她的身手可不比男人差；除此之外还有专职执行高空任务的开伞索（马龙·韦恩斯饰）、会七国语言的通讯员“断路器”（萨伊德·塔马乌伊饰）、军火专家“重载”（阿德沃尔·阿吉纽依－艾格拜吉饰）、沉默寡言的“蛇眼”（雷·帕克饰）等12名成员。他们开始追查行动，很快查明了“眼镜蛇”的的阴谋，而他们首先要解决掉的是给“眼镜蛇”提供军火支持的战争狂人的迪斯特罗（克里斯托弗·埃克莱斯顿饰）。而“眼镜蛇”组织里也有着不少心狠手辣的铁腕人物，例如外表性感漂亮，心肠却狠毒如蛇蝎的“男爵夫人”（<a target=\"_blank\" href=\"http://baike.so.com/doc/556091.html\">西耶娜·米勒</a>饰）就是当中的代表人物。除此之外，还有与蛇眼是兄弟的白幽灵（李秉宪饰）。\r\n	</p>\r\n	<p style=\"font-family:tahoma, arial, 宋体;font-size:14px;background-color:#FFFFFF;\">\r\n		　　G.J. JOE为阻止“眼镜蛇”的阴谋而与其展开大战，随着两方战斗的白热化，原本一直深藏不露的一位“眼镜蛇”重要成员也逐渐显露出真身，他的真实身份会令G.J. JOE的成员大吃一惊…\r\n	</p>\r\n<br />\r\n</span>\r\n</p>','我是好人',29,'1474.jpg',308,'','2013-05-16 09:26:24',NULL,0),(1475,'红尘客栈','<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	天涯 的尽头是风沙\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　红尘 的故事叫牵挂\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　封刀隐没在寻常人家 东篱下\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　闲云 野鹤 古刹\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　快马 在江湖里厮杀\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　无非 是名跟利放不下\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　心中有江山的人岂能快意潇洒\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　我只求与你共 华发\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　剑出鞘 恩怨了 谁笑\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　我只求今朝 拥你入 怀抱\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　红尘客栈风似刀 骤雨落 宿命敲\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　任武林谁领风骚我却 只为你 折腰\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　过荒村野桥 寻世外 古道\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　远离人间尘嚣 柳絮飘执子之手逍遥\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　檐下 窗棂斜映枝桠\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　与你 席地对座饮茶\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　我以工笔画将你牢牢 的记下\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　提笔 不为风雅\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　灯下 叹红颜近晚霞\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　我说缘份 一如参禅不说话\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　你泪如梨花洒满了纸上的天下\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　爱恨如写意 山水画\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　剑出鞘 恩怨了 谁笑\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　我只求今朝 拥你入 怀抱\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　红尘客栈风似刀 骤雨落 宿命敲\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　任武林谁领风骚我却 只为你 折腰\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　过荒村野桥 寻世外 古道\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　远离人间尘嚣 柳絮飘执子之手逍遥\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　任武林谁领风骚我却 只为你 折腰\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　你回眸多娇 我泪中 带笑\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	　　酒招旗风中萧萧 剑出鞘 恩怨了\r\n</p>','我是好人',26,'1475.jpg',284,'','2013-05-16 09:28:17',NULL,0),(1476,'烟花易冷','<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">繁华声 遁入空门 折煞了世人&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">梦偏冷 辗转一生 情债又几本&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">如你默认 生死枯等&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">枯等一圈 又一圈的 年轮&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">浮图塔 断了几层 断了谁的魂&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">痛直奔 一盏残灯 倾塌的山门&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">容我再等 历史转身&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">等酒香醇 等你弹 一曲古筝&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">雨纷纷 旧故里草木深&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">我听闻 你始终一个人&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">斑驳的城门 盘踞着老树根&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">石板上回荡的是 再等&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">雨纷纷 旧故里草木深&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">我听闻 你仍守着孤城&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">城郊牧笛声 落在那座野村&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">缘份落地生根是 我们&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">听青春 迎来笑声 羡煞许多人&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">那史册 温柔不肯 下笔都太狠&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">烟花易冷 人事易分&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">而你在问 我是否还 认真&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">千年后 累世情深 还有谁在等&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">而青史 岂能不真 魏书洛阳城&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">如你在跟 前世过门&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">跟着红尘 跟随我 浪迹一生&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">雨纷纷 旧故里草木深&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">我听闻 你始终一个人&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">斑驳的城门 盘踞着老树根&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">石板上回荡的是 再等&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">雨纷纷 旧故里草木深&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">我听闻 你仍守着孤城&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">城郊牧笛声 落在那座野村&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">缘份落地生根是 我们&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">雨纷纷 旧故里草木深&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">我听闻 你始终一个人&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">斑驳的城门 盘踞着老树根&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">石板上回荡的是 再等&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">雨纷纷 雨纷纷 旧故里草木深&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">我听闻 我听闻 你仍守着孤城&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">城郊牧笛声 落在那座野村&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">缘份落地生根是 我们&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">缘份落地生根是 我们&nbsp;</span><br />\r\n<span style=\"font-family:宋体, Arial, Helvetica, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">伽蓝寺听雨声盼 永恒&nbsp;</span>','我是好人',26,'1476.jpg',263,'','2013-05-16 09:30:12',NULL,0),(1473,'鹅鹅鹅鹅鹅鹅','咚咚咚7777','订单',29,'1473.jpg',272,'','2013-05-16 09:22:48','2015-06-26 07:06:22',1),(1477,'路遥《人生》','<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">《人生》给我最大的感觉是好像在写年轻人的青春故事，所以我更愿意它的书名是《青春》，而不是《人生》。</span>\r\n</p>\r\n<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">最初接触路遥的是《平凡的世界》，和《人生》一样都是以农村为主题，但是在反映生活的深度和广度上，两者都有不同的进展。《人生》的开篇引用了这样一句话：“人生的道路虽然漫长，但紧要处常常只有几步，特别是当人年轻的时候。”</span>\r\n</p>\r\n<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">《人生》中主人公高加林的经历是中国千万农村年轻人的一个缩影。在他身上自己也多少看到自己的影子，因为我也是土生土长的农村人，在毕业那年自己也曾经挣扎过、迷惘过、恋爱过。唯一不同的是没有像高加林和巧珍及亚萍的两种爱，和巧珍的爱是一种安全， 知根知底的安全感， 是每天工作劳累回到家，你可以放松的一种爱，她不会跟你谈哲学， 谈理想，谈梦想， 但是她会跟你唠家常， 她会为了家里的大小事务奔波，这种爱就像细水长流， 日子慢慢的过去， 爱会越来越浓。和亚萍的爱是是富有活力的， 他们每天在对方身上发现未知， 一起进步，当亚萍说出愿意抛下一切跟加林回农村，她是真心的， 只是她跟着他回到农村， 如何去适应农村的生活，加林再看回来 就会发现她已经不再是他爱的那个知书达理，有才华，有眼界的亚萍了，如果他们的激情无法转化为更脚踏实地的爱， 总有天激情会燃烧，烧焦然后变成灰烬。</span>\r\n</p>\r\n<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">书中还写了巧珍和马栓，亚萍和克南的爱，但是他们好像都是在给高加林的爱充当配角一样，没有轰轰烈烈，没有卿卿我我，是那么平淡又那么真实！</span>\r\n</p>\r\n<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">《人生》中除了年轻人的爱情，最触动我心灵的是农村人的勤劳、老实。德顺爷爷的对白我印象最深：</span>\r\n</p>\r\n<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">我，快七十岁的孤老头子了，无儿无女，一辈子光棍一条。</span><br />\r\n<span style=\"font-size:14px;\"> 但我还是天天心里热腾腾的，想多活它几年！</span><br />\r\n<span style=\"font-size:14px;\"> 别说你还是个嫩娃娃哩！</span><br />\r\n<span style=\"font-size:14px;\"> 我虽然没有妻室儿女，但觉得活着总还是有意思的。</span><br />\r\n<span style=\"font-size:14px;\"> 我爱过， 也痛苦过；我用这两只手劳动过，种过五谷，栽过树，修过路......</span><br />\r\n<span style=\"font-size:14px;\"> 这些难道也不是活得有意思吗？—— 拿你们年轻人的词说叫幸福。</span><br />\r\n<span style=\"font-size:14px;\"> 幸福！你小子不知道，我把我树上的果子摘了分给村里的娃娃们，我心里可有多......幸福！</span><br />\r\n<span style=\"font-size:14px;\"> 不是么，你小时候也吃过我的多少果子啊！</span><br />\r\n<span style=\"font-size:14px;\"> 你小子还不知道，我栽下一拨树，心里就想，我死了，后世人在那棵树上摘着果子吃， 他们就会说，这是以前村里的光棍老汉德顺栽下的...”</span><br />\r\n<span style=\"font-size:14px;\"> 当你经历了人生的沧桑， 你才知道幸福是什么， 有些东西要错过， 才会更懂得珍惜， 幸福，很简单就是活着， 你还能感觉的到自己活着，活着就是要经历痛苦， 才能感受到开心。</span>\r\n</p>\r\n<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">多么值得骄傲的农村老百姓，父辈们，我为你们而骄傲！</span>\r\n</p>\r\n<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">人生是什么？到底是什么？我无从回答。但是我知道，无论怎样我们的人生都应该精彩！这是我无意间看到的一句话，就是这句话让我更深的理解《人生》，更深的理解高加林这个农村年轻人的不可一世、自卑及无奈！</span>\r\n</p>\r\n<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">《人生》另一个看点就是真实的反应了一些为官的“后门之道”，高加林莫名其妙被辞掉教师，最后又由于叔叔的归来而莫名其妙的被重用。这不得不让我们这些从农村好不容易摸爬滚打出点成绩的年轻人感到愤怒和无奈！</span>\r\n</p>\r\n<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">在读《人生》的时候不得不佩服作者的见地是如此的深，因为他在写《人生》的时候年龄才33岁，也就是没比自己大多少，而他对生活的感悟却这样深！</span>\r\n</p>\r\n<p style=\"background-color:#ffffff;text-indent:2em;color:#000000;\">\r\n	<span style=\"font-size:14px;\">《人生》每个人读了之后都有自己看法，但是我相信读过《人生》的人一定会更珍惜自己现在的生活，因为人生并不是一帆风顺的，如果一切都是顺风顺水，那也就不叫人生了！希望大家去品一下《人生》！</span>\r\n</p>','我是好人',28,'1477.jpg',279,'','2013-05-16 13:09:31',NULL,0),(1478,'《活法》','<p>\r\n	<span style=\"background-color:#FFFFFF;color:#000000;font-size:14px;font-family:SimSun;\">《活法》是日本企业家</span><span style=\"font-size:14px;font-family:SimSun;\">稻盛和夫</span><span style=\"background-color:#FFFFFF;color:#000000;font-size:14px;font-family:SimSun;\">的代表作，原书由日本SUNMARK出版社出版，被誉为日本21世纪励志第一书。</span><span style=\"font-size:14px;font-family:SimSun;\">稻盛和夫</span><span style=\"background-color:#FFFFFF;color:#000000;font-size:14px;font-family:SimSun;\">是日本两家世界500强企业</span><span style=\"font-size:14px;font-family:SimSun;\">京瓷</span><span style=\"background-color:#FFFFFF;color:#000000;font-size:14px;font-family:SimSun;\">和KDDI的创始人，被誉为日本经营四圣之一，他是</span><span style=\"font-size:14px;font-family:SimSun;\">季羡林</span><span style=\"background-color:#FFFFFF;color:#000000;font-size:14px;font-family:SimSun;\">认为稀有的既是企业家又是</span><span style=\"font-size:14px;font-family:SimSun;\">哲学家</span><span style=\"background-color:#FFFFFF;color:#000000;font-size:14px;font-family:SimSun;\">的人物。《活法》的思想主要源自儒家和佛教，结合</span><span style=\"font-size:14px;font-family:SimSun;\">稻盛和夫</span><span style=\"background-color:#FFFFFF;color:#000000;font-size:14px;font-family:SimSun;\">经营的经历与感悟，形成了一个思想体系。</span> \r\n</p>\r\n<div style=\"background-color:#ffffff;color:#000000;\" class=\"para\">\r\n	<span style=\"font-size:14px;font-family:SimSun;\">《活法》主要内容简介：做企业的目的是什么？赢利！做企业人的目标是什么？帮企业赢利！然而，为什么在财务报表形势喜人、经营业绩节节攀升的同时，我们还会听到公司员工对老板的私下抱怨，看到高层们面对发展的十字路口无法舒展的愁眉，甚至此时正目睹着各界舆论对业界翘楚、时代精英们毫不留情、铺天盖地的指责与奚落？</span> \r\n</div>\r\n<div style=\"background-color:#ffffff;color:#000000;\" class=\"para\">\r\n	<span style=\"font-size:14px;font-family:SimSun;\">诚然，所有企业及企业人都恪守着赢利——这一亘古不变的原则在时代的浪潮中奋勇前进，但是又有多少人真正参透了利益背后的玄机？中国并不需要“道义放两边、利字摆中间”的商业大鳄，更不会提供给缺乏德行、忽视修为的企业人广阔发展的康庄大道；如果想在白热化的竞争中一枝独秀，就必须要剖开经济的表象，探求企业的本质，找到得以在如今的世道中生存下去的活法。</span> \r\n</div>\r\n<div style=\"background-color:#ffffff;color:#000000;\" class=\"para\">\r\n	<span style=\"font-size:14px;font-family:SimSun;\">《活法》的作者稻盛和夫是日本战后经济的传奇，他不用优美的文字取胜，也不用高深的说教让人五体投地，而是将其多年心得以质朴的文字向我们娓娓道来。企业人会从中领会真正的企业发展之路，而普通人亦将感受到最高境界的“为人之道”。</span> \r\n</div>\r\n<p>\r\n	<span style=\"background-color:#ffffff;color:#000000;\"></span><span style=\"font-size:14px;font-family:SimSun;\">&nbsp;</span> \r\n</p>','我是好人',28,'1478.jpg',345,'','2013-05-16 13:12:43',NULL,0),(1479,'致我们终将逝去的青春','<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">伴着一阵阵隐隐的心痛，看完了它。泪眼朦胧的挥别了郑微，挥别了年少时的那个“玉面小飞龙”。我知道她们终将要离开我，一转身，便是个诀别，今后都不会再见。即使多年后的梦里，当我们再次遇见那个曾经深深爱过的陈孝正，也只能说一声，我们再也回不去了，对不对？</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 青春终将腐朽，曲终人散时，才发现爱是那样铭心，而伤害却也同样刻骨。那个曾经如此飞扬跋扈的小飞龙，那个曾经如此孤傲冷峻的阿正，那棵老槐树，那本《安徒生童话》,那场7年的失约，那个陪你一起走过青春的阮阮，那个一生中最亮的月光，统统都将被埋葬。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 正如故乡是用来怀念的，青春就是用来追忆的，当你怀揣着它时，它一文不值，只有将它耗尽后，再回过头看，一切才有了意义――爱过我们的人和伤害过我们的人，都是我们青春存在的意义。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 每个人或多或少都可以在郑微身上找到自己当年的影子，每个年轻的女孩都会遇到自己生命中的阵孝正，而每一个陈孝正都要在经历过失去后才会蜕变成林静，而每个郑微在长大后都知道林静才是最终最好的选择。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成长的代价就是要失去一些东西，而那些东西当时不觉得有什么珍贵，若干年后，过尽千帆才明白这些将不会在以后的人生路上再次出现。可是没关系，每个人的成长之路都是如此，但愿我们失去后会懂得好好珍惜现在所拥有的一切，因为那些都是用青春，用眼泪，用心碎换来的。在未来的日子里，有过去的记忆温暖于心，甜蜜也好，痛苦也罢，都是存在过的证据。我的青春，你来过。虽然没有陪我走下去，但我是如此庆幸，在我一生中最美好的时光里，有你来过的痕迹，久久都不曾退去。这就够了。希望你日后想起，也会会心一笑，我们曾经的傻气。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 泪水早已落下。我的青春年华，我的欢声笑语，我的誓言哭泣，我的伤痕累累，我的诀别离去，如同一幕幕黑白</span><a href=\"http://www.manshijian.com/articles/category/dianying.html\">电影</a><span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">，慢慢变得清晰，再慢慢得灰飞烟灭。过去是如此的浓墨重彩，如今看来却只剩黑白。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 有人说我们的青春，并非说的是郑微，陈孝正和林静。而是指微微，阮阮，还有朱小北她们。见仁见智的问题。其实，谁没有过青春，就连许正阳回忆起自己的青葱岁月，也可以骄傲的说，当年我是如此的爱微微。还有老张，如此默默的感情，没有张扬，只有等待和祝福，又何尝不是另一种美好的青春呢？还是那句话，有谁没有过青春呢？又有谁的青春不会腐朽呢？</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 网上有人偏爱阮阮，那样的女孩，我想是大多数人喜欢的吧。爱情在她心里，是绚烂的烟花，一次就耗尽了生命，但是谁又有勇气抵挡那花开般的美丽呢？</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我想，我要去买书。在书的扉页写上“献给我已经腐朽的青春”。若我以后生个女儿，在她十八岁的时候，我会把书送给她，然后告诉她，我曾经爱过谁，又伤害过谁。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 正如那句很煽情的话，有些人注定是给我们怀念的。 在很多年以后，我还会想起这本书，想起它给我的眼泪、感动与领悟。 想起那些书中微小而细腻的情节，就像想起爱人的笑容那般温暖。想起那些年里的那些人，发生的那些故事。 想起是谁给了我们一刹那宠爱，却让我们用一辈子送他离开。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 致我们终将逝去的青春。</span>','我是好人',29,'1479.jpg',315,'','2013-05-16 22:25:56',NULL,0),(1481,'和宿命赛跑的人 -《暗战》','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">若是问起我最欣赏的中国导演，我可以不假思索的告诉你：杜琪峰。之前看影评人老晃关于《男人的争斗》（朱尔斯•达辛，1955 年）的评论，其中对托尼在进车站前朝玛杜转身一瞥的细节颇为赞赏，并称“当代中国导演，得此真传者，唯杜琪峰一人而已。”</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1999 年，杜琪峰拍了 2 部重要的</span><a href=\"http://www.manshijian.com/articles/category/dianying.html\"><span style=\"font-family:SimSun;\">电影</span></a><span style=\"font-family:SimSun;\">，《暗战》和《枪火》，《枪火》为杜琪峰拿到了 2 座最佳导演奖，2 部影片的主角刘德华和吴镇宇也分别斩获金像和金马影帝。然而在我看来，其重要性远远不止是拿奖这么简单。众所周知银河映像安身立命的资本便是其一系列警匪片中鲜明的银河烙印，这些烙印自《一个字头的诞生》开始，一脉相承，最终铸就了银河映像在香港电影中独树一帜的风格。然而稍加审视，不难发现在这一过程中某些元素得到了扬弃，其分水岭就在 1999 年。在这之前，铁三角之一游达志凭借作品《两个只能活一个》、《非常突然》、《暗花》占据银河半壁江山，也为早期银河作品奠定了命运无常和乖戾黑暗的主基调。1998 年，游达志在拍完《暗花》之后离开银河映像，铁三角分崩离析，杜琪峰要尽快重塑属于他一个人的银河映像风格之心，也在情理之中。而从今天看来，1999 年的杜琪峰不但做到了，而且做得非常漂亮。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:24px;background-color:#FFFFFF;\">回到《暗战》这部电影，看到标题观众就不免将其与《暗花》比较，加上上映时间仅仅一年半的间隔，把《暗战》视作《暗花》的非典型姊妹篇也未尝不可——两部作品都是把双雄之争作为影片发展的主线（《暗花》梁朝伟与刘青云，《暗战》刘德华与刘青云），所不同的是《暗花》充斥着让人感到窒息的肃杀之气，主人公都是被操控的棋子，所做的一切都是在命运棋盘上的困兽之斗，就像电影的英文片名 The Longest Nite 一样，透露的是无尽的黑暗与绝望。《暗花》里的宿命论，就像之前说的一样，命运无常，它是未知的，尽管可以感受到冥冥之中它无处不在，但却不知道究竟要把主人公牵引到什么地方。因此，可以说《暗花》是靠情节来展现宿命的，就像黄金时代的侦探小说，是用推理来带动情节，我们能做的就是跟着主人公一起，按照故事安排的线索，一步一步走向最终的结局。</span><br />\r\n<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 为什么说《暗战》对于杜琪峰而言，是他最重要的一部作品，是因为我觉得银河映像为人所津津乐道的宿命感，便是在《暗战》中定型的。首先，杜琪峰去除了游达志作品中宿命的“绝对权力”，使人不再沦为一具玩偶。其次，杜琪峰标榜的宿命是可知而不可逆的——这里的“可知”不是洞察，而是指在一定程度上的可预测性。同样，“不可逆”也绝非无能为力，事实上，杜琪峰之后的作品里，也很少出现《暗花》中阿琛（梁朝伟）这样一直坚信自己能掌控大局并打破宿命的主人公，而更多的是像《暗战》中刘德华这样，一开始便预知自己的宿命并坦然行走下去。</span><br />\r\n<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:24px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 这段话说起来很简单，但是放到电影中，表现手法就要进行彻底的改变。于是不得不提到我在第一段引用别人对杜琪峰的评价，然后，不同于《暗花》的靠情节来展现宿命，《暗战》想要传达出杜琪峰的宿命观，就必须要靠细节来建构整个故事。诚然，《暗花》中不乏令人过目不忘的细节，但不能否认这些对于整个故事的成立无关痛痒，可以说《暗花》像铺开一块黑色的画布，然后在上面画一朵浓艳的花，所有的细节不过是画布上的点缀，最终整个作品的完成，靠的还是线条。那么再认真看一看《暗战》里面的桥段吧：</span><span style=\"font-family:SimSun;\"></span>\r\n</p>','我是好人',29,'1481.jpg',255,'','2013-05-16 22:28:36',NULL,0),(1482,'向左走•向右走','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">“她习惯向左走，他习惯向右走，他们始终不曾相遇。”《向左走•向右走》是几米首次表现男女感情的长篇图文创作。男女主角彼此在生活中的巧妙关系，构成了 整个故事的设计。在页面的衔接上，有许多的巧思安排，在线条与上色上，作了更细致的处理，手法俐落有变化，是几米相当成熟的作品，也是代表作之一。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130509175630_21699.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">爱情的意象</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 《诗经•国风》的第一段：“野有蔓草，零露漙兮。有美一人，清扬婉兮。邂逅相遇，适我愿兮。”</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 辛波斯卡《一见钟情》的第一段：“他们彼此深信，是瞬间迸发的热情让他们相遇。这样的确定是美丽的，但变幻无常更为美丽。”</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 关于爱情的诗，古今中外惊人重复，关于爱情的故事一次一次，人们不知倦怠地在相遇的时候心动，然后再各自舔舐分别的痛。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 最恰切的爱情的意象是什么？在幾米的作品里，它是连贯统一、一脉相承的——萧瑟的希望，这个关于爱情的注脚有着“四两拨千斤”的力道。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 有人说，幾米的绘本是“城市里的乡愁”。乡愁是一种高尚的情怀；爱情却是一种通俗和普遍的一种情怀。将这两种情怀结合在一起，是幾米的天赋所在。幾米应该是对这一点很清楚：每个读者都有疲惫生活中的美丽爱情梦想。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130509175703_78626.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130509175712_16366.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">绘本之力</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 通过一种本身敏感的艺术形式，艺术家表述自己的情怀。显然，幾米的画风呈现着他“萧瑟的希望”的意向。绘本这个载体，如此成为了幾米最特别的方式，而这种介质，成为人们认知“相遇”的习惯。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 几米找到了一个维度，他以爱情的视角倏忽而入，借用绘本这个有力的介质，来表达自己对这个世界，或者对现有生活的认识。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 同时，爱情是让人“翻来覆去”的，这恰恰巧妙地与书的形式得到吻合。比起</span><a href=\"http://www.manshijian.com/articles/category/yinyue.html\"><span style=\"font-family:SimSun;\">音乐</span></a><span style=\"font-family:SimSun;\">、</span><a href=\"http://www.manshijian.com/articles/category/dianying.html\"><span style=\"font-family:SimSun;\">电影</span></a><span style=\"font-family:SimSun;\">这种线性的流淌下去的形式，翻一本书更让人回忆联翩。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在《向左走•向右走》中，我们能看到一个不保证结果但着实明确的希望。“爱情，总有一天会来。”这一点我们必须相信，但不能操之过急，不能将它当成一根稻草、一剂盖世良药为每日的生活疗伤。爱情，它只是总有一天回来。这也是“萧瑟的希望”最令人揪心的地方。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130509175736_47947.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130509175750_25046.jpg\" />\r\n</p>','我是好人',28,'1482.jpg',317,'','2013-05-16 22:29:58',NULL,0),(1483,'流行音乐告诉了我','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span style=\"font-family:Helvetica, Arial, sans-serif;color:#111111;line-height:19px;\"><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1173/20130502154217_36455.jpg\" /></span> \r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;color:#111111;line-height:19px;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"font-family:SimSun;\">在回答“知乎”上一个关于流行</span><a href=\"http://www.manshijian.com/articles/category/yinyue.html\"><span style=\"font-family:SimSun;\">音乐</span></a><span style=\"font-family:SimSun;\">评价体系的问题时，我说了这么一句：&nbsp;</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　“评价一张唱片，得放到他所属于的区域来看。也就是说，口水歌唱片要放到口水歌唱片来看。摇滚乐就要放到摇滚乐来看。”&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　一位叫做“宋大妈”的朋友瞧见了，于是他在自己的博客“武城路下段”就此写了一篇博文，名曰《没有两个标准》。他是这样说的：&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　“并不存在两个决然不同的艺术标准，达芬奇和贝多芬是不是真的没有可比性？左小祖咒和李宇春是不是真的无法在一个体系内评论？事实上，杰出如Michael Jackson，无论放到POP、摇滚还是R&amp;B，都是杰出的；而蹩脚如Coldplay、Green Day，无论拿了几座格莱美，无论把他们归为Punk、英式还是其它，也依然是蹩脚的。”&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　“人人都有欣赏和评价艺术的权利，但不是每个人都有足够的艺术判断力，艺术的标准最终是由少数人定义的，你可以不接受，也可以另起炉灶再搞一套，但它始终在那，并不会因为多了你的标准，就出现「多重标准」。所谓的「多重标准」其实就是没有标准。音乐分类学，及以此为基础的音乐评价体系（如格莱美），不是在评价作品，而是在迎合受众甚至蒙蔽受众。”&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　“分门别类的评价音乐、评价艺术，归根结底是审查与自我审查的产物。审查不仅有政治审查，还有存在更广泛的道德审查，以及对当代艺术影响更深的商业审查（「反商业」甚至成了商业音乐体系的一部分）。迎合掌权者、迎合权威、迎合受众、迎合诺委会评委在实质上并没有什么不同，因为艺术无论表现为什么形式，第一标准永远是独立意识基础上的自由创作。艺术的评论同样如此，「多重标准」这种小花招，看似客观，其实只是「审查者」给「自我审查者」上的套。”&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　我很想节选出最精华的部分，但我发现这三段话几乎是一字千金的。其实作者还有一句潜台词，我帮他说出来，那就是：别扯这些多重标准。所谓的多重标准只是为了达到某种（商业）目的的借口而已。比如，大家会想，一个只配听五月天的人，怎么同时还会是Belle &amp; Sebastian、RHCP、Radiohead、Eels、Francoise Hardy、Leonard Cohen、The Beatles、Bob Dylan的死忠。吃惯了山珍海味的人，竟然会在报纸上写张靓颖的新专辑很不错，如果这不是脑子缺根筋了，那背后一定有大家看不到的潜规则。&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　这位“宋大妈”其实人非常nice，巧合的是，我的另外一位朋友陈震先生竟然也和他相识。他其实并没有恶意的揣度。他只是反对为了达到商业目的而另外建立一套评价体系而已。于是，我非常希望知道他对《流行音乐的秘密》（Understanding Popular Music Culture）这本书的看法。&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　该书的作者为罗伊·舒克尔（Roy Shuker）。从资料上看，他是新西兰惠灵顿维多利亚大学媒体研究系副教授，主要的教学及研究领域为流行音乐和媒体政策。但我很想知道他在学院之外，是否有从事电台、报纸或杂志的媒体经验，此外是否有担任哪家唱片公司的顾问，否则他为何能够对流行音乐工业有这样全景的了解。&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　“以通俗音乐的商业属性为基础的定义，也包括一些被认为是以商业为导向的流派。许多评论家认为商业化才是理解通俗音乐的关键：‘当我们谈论通俗音乐的时候，我们谈论的就是以商业为导向的音乐。’”这几乎是全书的论调。我曾经在许多场合说，许多人听音乐，只听音乐本身，单纯地享受它的groove，研究音乐的文本，这非常棒。相比之下我很不单纯。或许因为我曾经是一位音乐媒体从业者（现在也是用文字影响着一些的人群），后来成为了一位音乐产业的从业者，也就是说，我有音乐的接受者/传播者/制造者的身份，“既是</span><a href=\"http://www.manshijian.com/articles/category/yundong.html\"><span style=\"font-family:SimSun;\">运动</span></a><span style=\"font-family:SimSun;\">员，也是裁判”（某朋友语），因此我眼中的音乐，除了审美之外更为复杂。2010年，我和iphen创办了音乐产业独立自媒体《乱弹山》，为的就是透过流行音乐的文本，去探究这个产业的各种规则，把他呈献给大家。当我看到这本《流行音乐的秘密》的时候，我必须得说：这本书就是我想说的——但作者比我高明太多。&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　iphen有一句口头禅，“流行音乐首先是一门艺术，然后才是一门生意。”我觉得这句话很棒。它点出了流行音乐区别于其他产业的关键之处。我们很少会用“产品”这个词去指代一张唱片，虽然流行音乐的价值是可以通过榜单、销量、歌手的商演价格、能开的演唱会规模来衡量的。但如果这是唯一的衡量标准，那当下的华语乐坛，最棒的歌手是否就是五月天？有比他们能赚钱的吗？&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　今年的华语音乐传媒奖上，我说年度艺人，你们竟然没有提名五月天？为什么？换个别人，在鸟巢开两场看看？我知道许多评委对我表示出无尽地笑意。但“两场鸟巢”这听似很傻的话背后还有很多。它包括：在缺乏乐团文化的华语流行音乐市场里，这个团体通过什么样的行为，为市场进行了定调；通常流行都有周期，但为什么这个乐团能够有长达10年或更长的市场周期？为什么这个团体的演唱会，来的观众都穿着他们的衣服？一个歌手如何通过一系列的市场营销，让无形的音乐冲破CD，成为泛商品化？巡演和演唱会对一张专辑来说是否是最好的推广手段？那为什么只有这个团体能够以纯粹的商演来cover掉利润单薄的宣传性演出？也就是说，这个团体无需任何宣传，或者说把宣传期直接压缩到最短，直接就是商演期（通常流行歌手的状态为长草期/创作期 - 制作期 - 宣传期 - 商演期的循环）？很多粉丝非常喜欢这个团体的歌词，那为什么现在的歌曲会有歌词，而最早的流行音乐是没有歌词的呢？音乐消费到底牵涉到了多少社会学和人口学？&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　我两次登上了五月天的《诺亚方舟》演唱会，但我没有被感动。感动对于我来说是不需要的。在我眼前是一台制作精良的</span><a href=\"http://www.manshijian.com/articles/category/dianying.html\"><span style=\"font-family:SimSun;\">电影</span></a><span style=\"font-family:SimSun;\">。导演非常精确地把握到现场观众们每一处的需求。这不仅是演唱会的rundown的问题，这包括了整个灯光舞美的设计，还有VJ，以及到了后来Coldplay演唱会上最先使用的会一起变换的LED手环（说实话我不同意宋大妈对Coldplay的评价）。用俗一点的话来说，很多人看五月天很感动，但我在诺亚方舟上看到的，是一艘载满了白花花银子的时代巨轮——你可以喷我铜臭味。&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　但《流行音乐的秘密》这本书就是这么一本充满了铜臭味的书。省点儿吧，都2013年了，还有人遮遮掩掩地讨厌钱吗？君子爱财，取之有道，且当流行音乐成为一门生意的时候，它必须被艺术化。书中提到了鲍勃·马利这个例子，非常漂亮。作者把鲍勃·马利的走红归咎为以下几个原因：欧美主流摇滚乐明星太贵（……）；鲍勃·马利的民族英雄标签非常合适年轻人们的口味（年轻人是流行音乐最大意义上的消费者）；原生态的雷鬼只需要换上键盘和电吉他就可以低成本地无缝嫁接到商业的审美范畴里；然后他们选了一张鲍勃·马利最温柔的相片，并对其进行波普处理。于是就有了我们今天的世界人民英雄。那么，你现在是否会嫌弃鲍勃·马利的铜臭味呢？你会因为披头四发第一张专辑的时候曾经为了冲上排行榜而买榜而耿耿于怀吗？&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　在这本书封底的推荐语中，我们可以看到李皖这样说：“流行音乐是一种工业制造。这个显在的事实或者被粉丝们匍匐在地而视而不见，或者被文化批判家高高在上一笔带过。显在，这个人细致而客观地剖开它，进入其制造业内部，展现其中的秘密。由此你发现，即便是摇滚英雄、文化全球事件，也并非那么纯粹冲崇高，当然，也并非只是一出提线木偶剧那么低劣。”在很多人的严重，李皖是一个连演出现场都不去的音乐文本研究者，但其实，若你和他面对面地多聊几次，你会发现他并不是。&nbsp;</span><br />\r\n<span style=\"font-family:SimSun;\"> 　　因此，我非常严肃地向所有热爱音乐、愿意更深入地了解音乐产业的人推荐这本书。现已上架。全国有售。</span>\r\n</p>','我是好人',28,'1483.jpg',264,'','2013-05-16 22:31:25',NULL,0),(1484,'因为跑才酷——跑酷','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130409182037_43027.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	　　跑酷亦称作“城市疾走”或Parkour。它诞生于80年代的法国，“Parkour”一词来自法文的“parcourir”，直译就是“到处跑”，此外它还含有“超越障碍训练场”的意思。Parkour把整个城市当作一个大训练场，一切围墙、屋顶都成为可以攀爬、穿越的对象，特别是废弃的房屋这项街头疾走极限<a href=\"http://www.manshijian.com/articles/category/yundong.html\">运动</a>， 非常具有观赏性。\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	　　这个运动是由法国的大卫·贝尔(David Belle)所创立的，它能使人通过敏捷的运动来增强身心对紧急情况的应变能力，这点和武术近似。不同之处，武术虚拟格斗反击，而跑酷虚拟紧急脱逃。跑酷非常具有观赏性城市疾走这项街头疾走极限运动，有点Free-running的意思，它们的区别在于Free-running更讲求表演---观赏性，而parkour是速度---实用性。配合猿猴一样的灵活攀越。有人认为是一门艺术。配合出其不意的动作绝对超出了常人的想象绝对是视听盛宴!不过这项运动的热衷者倒更愿意把它看成是一种青年亚文化所倡导生活方式。Parkour 不只是对身体有利，思想也是很重要的。当在练Parkour 的时候，你要非常的专注。学 Parkour会让人明白， 怎么克服自己的恐惧(Fear)，和加强克服困难的能力。人是不断提升自己和突破障碍的。\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	　　跑酷是人类根深蒂固的本能反应，这种本能就是运动，是一种需要真正勇士精神的运动。归根结底，跑酷是训练人体快速移动能力的一种自然的方式，它将我们周围任何可以利用的环境设施“为我所用”，这种“移动的艺术”既不需要特别的设备，也不需要专门训练。人体是从事这项运动的唯一的工具，对所有人来说，这就是运动的规则，因为它结合了所有人体的自然能力，比如跑、跳、攀爬等。这是一项能够开发你潜在运动能力的一项体育活动。\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	　　无论你在城市，还是在<a href=\"http://www.manshijian.com/articles/category/cunluo.html\">乡村</a>，这项运动要求你都要面对出现在你面前的障碍物，并“挑战它”。这就需要结合灵活性和控制性。运动装备很简单，一件T恤衫，一双轻便的跑步鞋以及一条运动裤。运动的时候，身体就像在驾驶舱的感觉一样，你会突然发现你能控制自己瞬间腾空的身体。\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130409182149_84279.jpg\" />\r\n</p>','我是好人',30,'1484.jpg',248,'','2013-05-16 22:32:07',NULL,0),(1485,'玄妙的舞步——鬼步舞','<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130415172350_72709.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;也许关于MJ的太空步很多人已经很熟悉了，现在我就向大家介绍并一种舞步——鬼步舞。发源于墨尔本曳步舞Melbourne Shuffle(Dance)，又叫曳步舞，又由于曳步舞在黑夜跳的时候疯狂速度快而让人看不清步伐才有了“鬼步舞”的别称，是一种拖着脚走的舞步，动作快速有力，</span><a href=\"http://www.manshijian.com/articles/category/yinyue.html\"><span style=\"font-family:NSimSun;\">音乐</span></a><span style=\"font-family:NSimSun;\">强悍有震撼力(主要是电子舞曲，多为Hardstyle风格)，舞蹈充满动感活力，极具现场渲染力，有着巨大的飘逸感而得名。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130415185432_95862.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">　　这种舞步20世纪80年代兴起于澳大利亚墨尔本一些地下舞场，其个性的舞步加上强劲的音乐以及个性的服装(荧光裤)，极其吸引人眼光由此逐渐流行开来;1992年，在海外其他地区舞蹈名字混乱这年澳大利亚人开始将此舞蹈称呼为Shuffle。名字被确定下来。 2002年12月，澳大利亚一家报纸在头版讲解Melbourne Shuffle，Shuffle首次出现在主流媒体。之后就流行到马来西亚和文莱(在这两个国家有Shuffle比赛)后传播到英国、德国、泰国等国家、借助于因特网(Internet)、网络媒体YouTube等在世界范围内流行开来。2005年，出版有DVD专题纪录片介绍墨尔本曳步舞。2006年，使其迅速提高声誉和知名度的媒体是YouTuBe视频网站。2008年9月6号，澳大利亚媒体network10报道Shuffle这种硬派风格舞蹈(HSD：Hard Style Dance)。2008年11月，“So You Think You Can Shuffle”舞蹈秀在youtube上展开。2009年“So You Think You Can Shuffle”舞蹈秀粉丝</span><a href=\"http://www.manshijian.com/parties/category/9.html\"><span style=\"font-family:NSimSun;\">聚会</span></a><span style=\"font-family:NSimSun;\">、比赛围绕在澳大利亚和德国等国展开。Melbourne Shuffle起源于上世纪80年代澳大利亚墨尔本的地下舞圈。舞蹈的基本动作就是不停切换双脚脚跟和脚尖。在随后的发展中，又加入了迅速的手臂动作，360度转圈，滑步等等。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">　　YouTube视频网站里最初Melbourne Shuffle是跟随着Techno音乐跳的，但随着时代的进步，Shuffle的音乐形式也从Techno变成了Trance和House。上世纪的90年代中后期，一部关于该舞蹈的纪录片使得该舞蹈获得了一定的关注。直到2006年，有外国舞者将自己跳Melbourne Shuffle的视频传到了Youtube之后，该舞蹈才开始走红。近日，</span><span style=\"font-family:NSimSun;\">优酷网还有一段</span><span style=\"font-family:NSimSun;\">视频名为《徐州农民工也疯狂，小跳鬼步舞》，上传于2013年4月7日，上传者网名叫“徐州无悔”。一是舞步确实不错，二是头戴安全帽、手拿水泥铲、身穿迷彩服的装扮给他加了分，所以这段视频很快就火了起来，一天被点击了40多万次。有才的网友点评称：“大风起兮尘土扬，农民兄弟亲上场，刚劲有力鬼步舞，众人皆为他鼓掌，人间处处有高手，能歌善舞会抹墙。”</span>\r\n</p>','我是好人',30,'1485.jpg',258,'','2013-05-16 22:33:27',NULL,0),(1486,'有氧瑜伽','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;</span><a href=\"http://www.manshijian.com/articles/article_search/55Gc5Ly9.html\"><span style=\"font-family:NSimSun;\">瑜伽</span></a><span style=\"font-family:NSimSun;\">是东方最古老的强身术之一，它起源于印度，流行于世界。传说公元前古印度高达8000米的圣母山上，印度先贤们在深沉观想和静定状态下，从直觉了悟生命的认知。瑜伽一词源于梵文音译，是结合、联合、统一的意思，这也是瑜伽的宗旨和目的——将思想和肉体结合至最佳状态,把生命和大自然结合到最完美的境界。</span><br />\r\n<span style=\"font-family:NSimSun;\">　　印度瑜伽不仅是古印度文明在艺术、哲学、医学领域的奇迹，更已成为世界文明的瑰宝。因其完备的科学性、高度的艺术性和独特的智慧性，已延续5000年并流传至今。20世纪以来，经东西方深入的科学研究，它的神奇功用进一步证实，已被西方研究证明是人类最适宜、最有效的修身习练法之一，这一集文化、艺术、哲学、医学的</span><a href=\"http://www.manshijian.com/articles/category/yundong.html\"><span style=\"font-family:NSimSun;\">运动</span></a><span style=\"font-family:NSimSun;\">方式再次风靡各国，进入了历史上又一个繁盛时期。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/articles/20121203/images/20121203024331895583u5ut7.jpg\" style=\"width:600px;height:776px;\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 作为一种非常古老的能量知识修炼方法，“瑜伽”并非只是一套流行或时髦的健身运动这么简单。现代人汲取其有益精华，发现瑜伽的好处不胜枚举：瑜伽能加速新陈代谢，去除体内废物，形体修复、调理养颜从内及外;瑜伽能带给你幽雅气质、轻盈体态,提高人的内外在的气质;瑜伽能增强身体力量和肌体弹性，身体四肢均衡发展，使你变得越来越开朗、活力、身心愉悦; 瑜伽能预防和治疗各种身心相关的疾病，背痛、肩痛、颈痛、头痛、关节痛、失眠、消化系统紊乱、痛经、脱发等都有显著疗效;瑜伽能调节身心系统，改善血液环境，促进内分泌平衡，内在充满能量。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">最重要的是：</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">瑜伽能消除烦恼——减压养心，释放身心，全身舒畅，心绪平静，冷静思考,达到修心养性的目的; 　　</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">瑜伽能提高免疫力——增加血液循环，修复受损组织，使身体组织得到充分的营养; 　　</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">瑜伽能集中注意力——是学生及压力人群提高学习及工作效率的最佳休息法、锻炼法</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">…… 　　</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">瑜伽能让你跳出心灵的限制，从而更好地回归角色，并坦然迎接生活中的一切挑战。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/articles/20121203/images/20121203025509674760hr9y9.jpg\" style=\"width:600px;height:872px;\" /><span style=\"font-family:NSimSun;\"></span>\r\n</p>','我是好人',30,'1486.jpg',236,'','2013-05-16 22:33:54',NULL,0),(1487,'带着烤箱践行梦想','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">用快乐搅拌鸡蛋，带着梦想打发奶油，</span><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 用甜蜜点缀花边，最后借助烤箱的热情烘焙出幸福。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; —— 邓小牛</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在小牛很早的一篇博客里看到这句话——“多希望我是盏烛光，在你需要的时候发亮。”我甚至不知道她是在什么心境下写出的这样一句话，只觉得心里很暖，她一定是愿意为爱发出能量的女孩，知道珍惜，懂得付出，用快乐搅拌鸡蛋，带着梦想打发奶油，用甜蜜点缀花边，最后借助烤箱的热情烘焙出幸福。她是厨子邓小牛，广东人，27岁的娃娃脸，现在是小会计一枚，热爱烘焙。她说：生活不可能像你想象的那么好，但也不会像你想象的那么糟。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1540/20130510120226_67080.jpg\" />\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; “生活不可能像你想象的那么好，但也不会像你想象的那么糟。”</span>\r\n</div>\r\n<br />\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 有多少喜欢烘焙的孩子是因为家乡没有华丽丽的甜品店，于是开始四处网购，原料、工具、模子一样都不能少，最好从4寸的到9寸的各来一个，辛辛苦苦满足自己欲壑难填的口腹之欲。在经过各种成功与失败、褒奖与吐槽、蛋白打发过硬和忘了加小苏打事件等，终于渐入佳境，亲人捧场，好友助阵，在下厨房的烘焙作品也已渐成规模。关于烘焙，小牛说：可能因为自己对这方面十分感兴趣，会很有耐心地去做，所以每次尝试新的甜品一般都能一次成功。还有最大的功劳是那本《跟着君之学烘培》，刚接触烘培时，全靠这本书让她了解了最基本的烘培知识，之后就是慢慢积累经验，做多了自然就熟悉了。想做什么甜品，只要上网搜索，按照教程的步骤一步步去做都会成功，所以小牛觉得得：烘培，最重要的就是耐心。</span><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp;</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1540/20130510120234_32604.jpg\" />\r\n</div>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">只要有耐心，著名法式甜点马卡龙也可以挑战成功。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 小牛作为一个典型性双鱼，浪漫且富于幻想，对生活充满热情与希望。她说她经常晚上睡前想第二天要做什么样的甜点，怎么装饰，如何摆盘，配一块什么花色的桌布，竟然会想到失眠。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 一年前，小牛结束了在外漂泊的生活，又随丈夫回到了家乡工作，以前就隐约觉得自己会对厨房感兴趣，但苦于一没时间二没空间。婚后生活安稳自在，自然就要顺应条件，一一去实现，哪怕为了一个蛋糕在厨房呆一天也是快乐充实的。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp; &nbsp; &nbsp; &nbsp; 虽然照着方子按部就班，最大程度保证了成功率，但是偶尔也会想自己突破一下。于是有一次小牛勇敢地改了方子。本来就是做一个普通的戚风蛋糕，心想加些</span><a href=\"http://www.manshijian.com/articles/article_search/5ben5YWL5Yqb.html\"><span style=\"font-family:NSimSun;\">巧克力</span></a><span style=\"font-family:NSimSun;\">酱会不会很好吃呢？不然快过期了就浪费掉了。加上，搅拌，入箱烘烤，看上去一切正常，正在期待脱膜后会是什么样时，蛋糕突然爆浆了，瞬间傻眼，弄得自己也是苦笑不得。本来以为还会像在制作过程中犯了错而不小心诞生的布朗尼蛋糕和甘纳许巧克力一样，成就意外美味，没想到结果只是成了一个超大号的熔岩爆浆蛋糕。不过味道还好，巧克力是可以拯救一切的。所以，烘焙的乐趣就在于：用精妙的计算去幻化出平衡的味道，用一丝不苟的步骤去成就丰富的口感。细心地去关注面团的状态、果酱的浓稠，就算搅拌的手法不对也会影响成败，只有足够的耐心与热情才能烘焙出真正的美味。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1540/20130510120259_65318.jpg\" />\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp; 邓小牛做的甜点：黄油小蛋糕、老式面包</span>\r\n</div>\r\n<br />\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;邓小牛除了烘焙，对家常菜也越发有兴趣了，各种小炒、烤箱菜慢慢地也开始学起来。如果说烘焙是奇妙的幻想，家常菜是过日子的平实，那么幸福已经在这个姑娘的生活里开出了花朵。</span><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;邓小牛最后说：生活中有我最爱的烘培，我愿意每天都能够呆在厨房里，亲自动手烤出一盘盘美味的食物。烘培不仅给我带来无限乐趣，还为我的生活添了一抹美丽的色彩，希望能够与大家分享我的这份小快乐。</span>\r\n</p>','我是好人',25,'1487.jpg',237,'','2013-05-16 22:34:57',NULL,0),(1488,'欧式面包物语 美味口感大比拼','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">无论是佐餐还是作为小食点心，面包已经完全融入了我们的日常生活。许多人经过喷香四溢的面包店时都会忍不住驻足留意一番。</span><a href=\"http://www.manshijian.com/articles/category/dianying.html\"><span style=\"font-family:NSimSun;\">电影</span></a><span style=\"font-family:NSimSun;\">里男女主角抱着纸袋，装着法式长棍的场景也已然时常见于生活中。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">　　面对琳琅满目、种类颇多的面包，总有一种是你钟爱的。虽然分类方法不同，但就整体的质地和口感而言，可以将面包分为软式面包、硬式面包和丹麦类(起酥)面包三大种。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1582/20130513142736_38644.jpg\" />\r\n</div>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 软式面包</span></strong><br />\r\n<span style=\"font-family:NSimSun;\">　　这种面包讲求式样漂亮，组织细腻，以糖、油或蛋为主要配方，以便达到香酥松软的效果。软式面包以日本制作的最为典型。面包的刀工、造形与颜色，均十分讲究，尤以内馅香甜，外皮酥软滑口，更是吸引人;至于美国，则是重奶油与高糖。台湾由于曾受日本殖民统治，因此在面包食用的习惯上，仍以日式软面包为消费主流。软式面包多采用平盘烤箱烘烤。</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1582/20130513142740_85036.jpg\" />\r\n</div>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\"font-family:NSimSun;\">硬式面包</span></strong>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">　　欧洲人把面包当主食，偏爱充满咬劲的“硬面包”。硬式面包的配方简单，着重烘焙制程控制，表皮松脆芳香，内部柔软又具韧性，一股浓郁的麦香，愈嚼愈有味道。硬式面包最普遍有德国面包、法国面包、英式茅屋面包、意大利面包等多种。欧式面包则采用旋转烤箱，因此于烘焙初段时可喷蒸气，除可使面包内部保水率增加，又能防止面包表面干硬。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;丹麦面包</span></strong>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">　　丹麦面包又称起酥起层面包，口感酥软、层次分明、奶香味浓、面包质地松软。这种面包的发源地是维也纳，所以在现在的其他产地，人们称之为维也纳面包。丹麦面包的加工工艺复杂，经过搅拌和发酵之后，将经过3h以上低温发酵的面团滚压成厚约3cm的面片，再进入折叠工序，使包入面团中的油脂经过该工序产生很多层次，面皮和油脂互相隔离不混淆。出炉后表面刷油，冷却后撒上糖粉或者果酱来装饰。因为制作时间长，这类面包的款式相对较少，常见的有牛角包、果酱酥皮包。</span>\r\n</p>\r\n<div class=\"clear\" style=\"padding:0px;margin:0px;border:0px;font-size:1px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;background-color:#FFFFFF;\">\r\n</div>\r\n<div class=\"botton\" style=\"padding:0px;margin:10px 0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<ul>\r\n		<li class=\"click_zan\">\r\n		</li>\r\n	</ul>\r\n</div>','我是好人',25,'1488.jpg',250,'','2013-05-16 22:36:17',NULL,0),(1489,'远方的朋友','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1391/20130509121029_91440.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;滇西北的冬天不是一般的冷。在以前取暖设施不发达的时候，藏区居民没到冬天要成群结队拖家带口跑到南方要过冬，直到现在还有这种现象出现。大理洱源县的牛街镇就是藏民们泡温泉过冬的好去处。我的朋友是以为黑黢黢的藏族爷们儿，他从小就跟着家人冬天的时候去牛街泡温泉。泡温泉在他看来只是一个季节性行为，如同秋天一群大雁往南飞的景象一样，他不敢到任何稀奇。我们一起读书的时候他常常听人家说温泉如何如何，怎么怎么昂贵，他常常以一种居高临下的姿态嗤之以鼻，这让很多人感到难堪，包括我。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我这一次来到滇西北原本抱有一种无所事事随处游荡的态度，我知道自己从哪儿来，却不知道要到哪儿去，也不愿意知道。我认为，目的性太强会失去</span><a href=\"http://www.manshijian.com/articles/category/lvxingzhe.html\"><span style=\"font-family:SimSun;\">旅行</span></a><span style=\"font-family:SimSun;\">的本质意义。比如说吧，我们都属于一帮乐于欣赏娱乐节目的人，然而做娱乐节目的人其中的苦乐远非我们所能想象。我认识的一个做娱乐节目的，后来辞职，说是压力过大，有抑郁倾向。这是一种讽刺。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; 冬天的时候，德钦的荒凉比以前严重的多。以前只是单纯的荒凉，现在是能荒凉到什么程度就荒凉到什么程度，肆无忌惮的荒凉。唯一让人感觉有点生气的就是山坡上一小片一小片被周围山势挤压得难受的青稞田或者麦田。我不知道该如何区分他们，反正，冬天这个时候，山坡上种植的要么是小麦，要么是青稞。这是当地的粮食作物。然而，这种状况近年来有所变化。藏民们有钱了之后，发现其实稻米要比青稞和小麦好吃的多。于是他们开始大量从大理或者楚雄购买大米，然而小麦和青稞也没有完全扔掉。青稞用来酿酒，小麦用来作饲料。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1391/20130509123037_80956.jpg\" />\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:1.5;font-family:SimSun;\">&nbsp; &nbsp; &nbsp; 在去我朋友家的路上，我们遇到了他的以为骑驴邻居，一个胖墩老人，一副愁眉苦脸的样子</span><span style=\"line-height:1.5;font-family:SimSun;\">。脸上的表情与他的身材和面向十分的不合谊。我们跟他打招呼，他点头回应。就在我们继续前行的时候，他叫住了我们。问能不能载他一起回去。我朋友问，载你肯定没问题，但是载驴肯定不行。老人说，那你就先管我，不要管驴，驴自己能回去。老人还是，他走了一天了，驴受得了疲惫，他老了，不行了。于是，我和我的朋友还有这位胖胖的老人三人骑着摩托车回去。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; 回去的途中我和老人聊天，发现他不是他表现出来的那样忧心积虑，相反，他是一个欢乐的胖子。他说，现在的人越来越不像话，他去城里买药材，给人骗了，收到了假钱。我朋友听到后大怒，说这还了得。建议老人下次带刀去，遇见那家伙，活劈了他。我向来是赞成暴力手段，可是这个时候也不能明确凡是反对，因为这样会影响他们的心情，影响我朋友胸腔里某种泛滥肆虐的情绪。我曾经害怕过这种情绪，因为那种情绪一旦发作起来，可以不顾一切，像兽和兽之间完全以性命相搏的情绪。那是一种不好的情绪，可是在这种粗犷的地带却可以合情合理的存在。这是自然选择的结果，目前还轮不到我来指手画脚。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1391/20130509124540_11705.jpg\" />\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; 我们很快到达了目的地，一个无比巨大的山谷。我以前来过，这次也像之前一样吃惊。整个山谷将村庄怀抱住，将外面肆虐的寒风阻挡在山口之外。我朋友说，这个山谷以前水源不较好，现在有人在山后开矿，把水源毁掉了。为此，他们给矿上的人打过好多次架，最后由政府出面解决。政府出资为他们修建引水管道，企业每年给他们一定的分红来补偿他们的损失。即使这样，他们也是愤愤不平，认为厂矿和政府都是一群强盗。我对此不置可否，因为有些事情是无法用道理来争论的，很多很有道理的事情在特定的情况下会变得匪夷所思。所以，我对此保持沉默。当所有人开始陷入沉寂的时候，我感觉又开口的必要。我说，我饿了。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; 我朋友马上做出一副惭愧的表情，那个胖老人</span><span style=\"font-family:SimSun;\">在山谷口就下车然后</span><span style=\"font-family:SimSun;\">屁颠屁颠的跑回家去拿一些吃的东西去我的朋友家聚聚。我朋友说，他家有好吃的腊肉。我们很快下到谷底，等待我们的，是一场久违的欢乐。</span>\r\n</p>','我是好人',27,'1489.jpg',232,'','2013-05-16 22:38:09',NULL,0),(1490,'厦门大学：走在最浪漫的校园','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">厦门大学近日宣布从六月起禁止</span><a href=\"http://www.manshijian.com/articles/category/lvxingzhe.html\"><span style=\"font-family:NSimSun;\">旅行</span></a><span style=\"font-family:NSimSun;\">团进入校区。</span><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 五月，穿越泉州，曾在厦大中转住宿，有幸在厦大走马观花浏览一番。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 厦大，这里称为最浪漫的校园。曾流传，隔壁南普陀寺一僧人，因爱恋厦大一位女生而还俗，回到滚滚红尘，再续前缘！</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143133_94753.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143143_41401.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143153_19922.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143208_41379.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 大学里有多情的女生，有敢爱的男生，还有最浪漫的故事，如果将一生最美好的时光，最浪漫的故事放在这里，也不枉你我青春一场。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143235_47521.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143241_21548.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 芙蓉湖畔，一座男生的宿舍称为芙蓉楼，也许只有在这样的海滨校园才能让男人的世界也如此委婉、诗情！</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143440_94837.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143453_51377.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 芙蓉湖畔，椰林丛中，有多少美好年华的爱恋在这里上演，相携伴读，又有多少故事能走得更远。</span><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 走的匆匆，看的也很肤浅，拍摄了一些美景与大家分享。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143554_45780.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143605_76409.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143612_75044.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143624_74090.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1507/20130515143630_35417.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 新鲜旅厦门大学出行攻略：</span></strong><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在厦大内漫步，您可以看到有名的部分戏剧拍摄场景。</span><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 厦大芙蓉湖→建南大会堂（戴斗笠穿西装，陈嘉庚先生最喜欢的一种风格）→芙蓉隧道：隧道里的墙壁上，布满了厦门大学历届毕业生的</span><a href=\"http://www.manshijian.com/articles/article_search/5raC6bim.html\"><span style=\"font-family:NSimSun;\">涂鸦</span></a><span style=\"font-family:NSimSun;\">作品，请一定要找到它，它一定会给你带来十足的惊喜和收获。→情人谷（厦大水库）：原来只是一个普通的水库，但由于周围风景宜人，变成了厦大情侣约会散步的好去处，从而美名其曰“情人谷”。</span><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中午时段，可以在厦大内的食堂或者咖啡厅用餐，厦大食堂的</span><a href=\"http://www.manshijian.com/articles/article_search/576O6aOf.html\"><span style=\"font-family:NSimSun;\">美食</span></a><span style=\"font-family:NSimSun;\">也是出了名的好吃哦。</span>\r\n</p>','我是好人',27,'1490.jpg',333,'','2013-05-16 22:38:50',NULL,0),(1491,'最好的时光在路上--迷路鼓浪屿','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">“最好的时光在路上——在不同的地方遇见的不同的人，他们各不相同的人性闪烁，在那个与你交错的瞬间，改变、点化、充盈着你的人生。”</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135020_46037.jpeg\" /><span style=\"font-family:NSimSun;\"></span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; “最美的自己在远方——孤独的</span><a href=\"http://www.manshijian.com/articles/category/lvxingzhe.html\"><span style=\"font-family:NSimSun;\">旅行</span></a><span style=\"font-family:NSimSun;\">能让人有更多的机会单独面对自己，向内心的更深处出发。总有一天，你会在不经意之间，遇见那个你最想成为的自己。那个你，卸下了在都市丛林里背负的重重铠甲，活力蓬勃，宛若新生。”</span><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 这次的厦门之行，没有地图，没有攻略，只想在这个城市一个人逛逛，确定是否以后会在这里工作。出行之前，大概搜了搜“鼓浪屿”、“曾厝垵”、“集美”、“厦大”、“南普陀”等几个关键词，在网上订了前两天鼓浪屿上面的住宿，就这样，一个人来了。对我来说，旅行的意义在于：在游游历历之中，更加清楚自己想要的生活。</span><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\"font-family:NSimSun;\">DAY 1：媛媛同学的故事</span></strong><br />\r\n<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 12点30左右到厦门，在机场拿了份免费的地图，出门右拐约50m处坐机场巴士到轮渡码头，买A票坐船上鼓浪屿。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 住宿订的是屿岸花（碟子的故事）。对于没做攻略一路靠记忆和问路的方法找地方的我这个地方还真难找。第一个人告诉我应该右拐。好吧，我往右走。途经潘小 莲，停下来买了一个酸奶，收银MM告诉我应该往左走。。。好吧，我往回走。走了好久好久，再问路发现走反了。纠纠结结，花了半个小时，终于找到三明路了。这是一条很安静的小路，有人在拍婚纱照，一个蓝裙子的女生坐在路边画画。她已经在岛上住了6天了，粉红色的速写本快画满一本。阳光洒在她的画本上，她静静地画着，一下子，我爱上了这个地方。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135509_12159.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135519_83938.jpeg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 很快找到了屿岸花，没让我失望。前台和老板（应该是老板吧）人都很不错，房间也很温馨。放下行礼，带上相机，出去迷路了</span><br />\r\n<span style=\"font-family:NSimSun;\">屿岸花--碟子的故事</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 回去找画画那女生已找不到了。于是我沿着内厝澳路往下走。在金兰饼家买了一个椰子饼，据说当地人都在这家买。确实不错，2元一个，接在手里还热热的，非常好吃。就在这里，碰到了拿着盖章本找店的媛媛同学，反正我也不认路，大家又都是一个人，于是就跟她一起找店了。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135709_34089.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135723_86434.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135748_69559.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135802_26561.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135820_82988.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135828_47372.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135844_74920.jpeg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我们一路走，一路逛，一路聊。聊起了以后最想做的事，发现所有女人都有一个梦想：开一间自己的咖啡店。我们畅想着，以后在鼓浪屿上合开一家家庭</span><a href=\"http://www.manshijian.com/articles/category/kezhan.html\"><span style=\"font-family:NSimSun;\">旅馆</span></a><span style=\"font-family:NSimSun;\">，一楼做咖啡厅加酒吧，她负责设计、调酒。。。我负责。。。。衍生出来，所有女人都有一个梦想：开一间自己的店，也许是咖啡厅，也许是蛋糕店，也许是。。。我想这是所有人对自由的向往吧，有自己的小店，自己做主，想自己所想，爱自己所爱。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 龙山洞里一个歌手唱着《北京，北京》。洞里面的混响很不错，我们坐着听完这首歌。不知道为什么，对于这样的歌手，我总是觉得特别尊敬。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515135926_96740.jpeg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 终于媛媛同学盖完所有的章了。佩服啊，这是个大工程。她带我去吃了她认为很好吃的肠粉。领着我去她印象深刻的小店。没听她劝买了一杯张三疯奶茶，20元，后悔。接着我们沿海边走，讲着她这一段旅程的经历。她是一个东北的女孩，太阳处女座，上升水瓶座，热爱自由的女子。半年前，她背起了自己的登山包开始了自己的这一段旅程。从苏州到上海到。。。厦门， 从婚纱店到酒吧调酒师到。。。。就这样一路打工一路旅行。一路上的点点滴滴，不是一个晚上或者一两天可以讲完的，那些经历是存在心底以后慢慢回味的。晚上 和朋友打电话，讲起了媛媛同学的故事，她很是羡慕。是啊，有很多人都渴望着能这样挎起自己的行囊逃离办公桌去追寻自己理想的生活，可是，又有多少人有勇气 跨出第一步呢。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140023_11962.jpeg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\"font-family:NSimSun;\">&nbsp;DAY2：照片的故事</span></strong>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在我看来，鼓浪屿最美的时候是早晨。每天醒来，拉开窗帘，都有阳光洒进来。这时岛上的人很少，有紫色的牵牛花，听得到隐约的钢琴声，在鸟语花香中漫步，多美好的一件事啊。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140122_94860.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140128_56665.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140138_42249.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140148_80716.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140157_48651.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140206_33892.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140219_50723.jpeg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 下午叉叔来了，终于有人给我拍照片了。叉子同学是厦门人，跟拍加导游，处女与天秤的交界。天秤座是我的贵人，处女座嘛。。。就不好说什么了。叉叔人很nice，服务意识超强，拍片很专业，装备是5DII，一路上讲了很多关于鼓浪屿的故事。让我很惊讶的是，叉叔居然是80后，还是靠近85的同学，哎，叉叔莫怪，只是突然想起同为处女座的媛媛同学也看起来超出她的年龄，难道处女座都比较成熟？据说叉叔以后可能要转行卖烧烤或者是开旅店了，放心吧，你口碑这么好，开什么都人缘好吧，以后该改口叫叉总了。</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140405_42216.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140417_87622.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140420_43103.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140430_42251.jpeg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1496/20130515140439_13057.jpeg\" />\r\n</div>','我是好人',27,'1491.jpeg',277,'','2013-05-16 22:39:47',NULL,0),(1495,'雅典国家考古博物馆','<p align=\"left\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">文章导读：爱</span><a href=\"http://www.manshijian.com/articles/category/lvxingzhe.html\"><span style=\"font-family:SimSun;\">旅行</span></a><span style=\"font-family:SimSun;\">的人中，有一群人是逛博物馆的狂热fans，他们会在旅行时，去不同地方的博物馆泡上很久，有的甚至会因为一个展览专门前往某地旅行。其实，若想探索欧洲最有文化底蕴的城市，博物馆绝对应该是你的最佳起点。国家考古博物馆之于雅典、梵蒂冈博物馆之于罗马、大英博物馆之于伦敦，普拉多博物馆之于马德里，恰如王冠上那一颗最名贵的钻石，折射着永恒的魅力之光。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130510114031_43717.jpg\" />\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;国家考古博物馆&nbsp;&nbsp;</span>\r\n</p>\r\n<p align=\"left\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 对热爱古希腊文化的现代人而言，雅典国家考古博物馆是满足他们梦回古典时代的绝佳通道。博物馆收集了希腊各地出土的各个时期价值极高的文物，数量将近2 万件，时间跨越了远古时期至罗马帝国时代，可谓集古希腊文物之大全。</span><br />\r\n<span style=\"font-family:SimSun;\">　　 一进入口先到了藏有数千件迈锡尼时代文物的迈锡尼展室，荷马在史诗中对迈锡尼的描述为“遍布黄金”，展品中确实黄金制品众多，非常精美。其中尤以传说中的 阿伽门农黄金面具最为有名，它是强盛一时的迈锡尼文明的最好证明之一，也是考古博物馆的镇馆之宝，每个来这里的参观者都要前来膜拜一番。&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130510114455_72981.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 阿佛洛狄忒与潘（牧羊神）的大理石雕塑</span><br />\r\n<span style=\"font-family:SimSun;\">　　都说古希腊造型艺术的代表非雕塑莫属，博物馆藏品中最多的便是青铜和大理石雕像，内容皆出自古希腊神话，它几乎占据了博物馆一层外围整整一圈。如果你有时间慢慢逛上一圈，就能清晰地了解希腊雕塑是如何沿着几何主义- 古朴主义- 古典主义- 希腊化风格的道路逐步发展演变的，你更会为那些细致精巧、栩栩如生的雕像形态所折服。第15 室的海神波塞冬铜像是其中不可错过的精品，这位天神宽肩厚胸，双腿矫健，左臂自信地直伸，右臂微微上举，动作定格在即将掷出雷电的一瞬间，匀称比例和肌肉动态堪称无懈可击，让人不得不感叹古希腊人在雕塑艺术上达到的高度。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 有时间的话，还可以去战后新建的陶器和陶瓶陈列区逛逛，那里展出了希腊艺术里优美精细的陶瓶及罐、壶、杯、碗等各式陶器，其数量之多、质量之好堪称世界之最。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130510114630_10313.jpg\" />\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">原始时代陶器及人像</span>\r\n</p>','我是好人',27,'1495.jpg',243,'','2013-05-18 01:46:26',NULL,0),(1496,'中国合伙人 ','<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:26px;background-color:#FFFFFF;\">&nbsp; &nbsp; 这是个激荡三十年，关于一个土鳖，一个知识分子，还有一个浪漫骑士的\"中国式成功\"故事。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:26px;background-color:#FFFFFF;\">　　成东青（黄晓明 饰），农村出身的土鳖，两次高考落败，眼看就要屈服于当农民的命运，他最后一搏，搏命背下整本英文字典，从明眸变成近视眼，第三次试考，考上北大；孟晓骏（邓超 饰），精英知识分子，强烈自信，内心认定自己永远是最优秀的那个。王阳（佟大为 饰），八十年代的浪漫派，样子俊朗，热爱文学，一生梦想是当个诗人。这样三个各走极端的人，居然就在北大碰上，戏剧性的建立友谊。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:26px;background-color:#FFFFFF;\">　　三人跟八十年代莘莘学子一样，怀抱着一个美国梦。申请签证的结果是：两个成功，一个被拒。土鳖成东青延续失败的命运，眼看两个好友出国完梦，他失望透顶，他只有留在北大任教，却又因在外私自授课，被校方发现，央求悔罪仍被除名，毫无余地的成为一个真正\"失败者\"。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:26px;background-color:#FFFFFF;\">　　美国那边，孟晓峻正要一展抱负，却未料堂堂北大高材生，在美国根本找不着工作，落得在餐馆当侍应助理的命运；王阳签证成功，却因一个一见钟情的美国女孩放弃出国，贯彻其浪漫派个性；成东青一无所有，只有偷偷在肯德基办补习班，其独特的自嘲教学法，却渐渐吸引不少学生。这是命运的安排，他从没想过，被拒签这个人生最失败的挫折，亦是成就他人生中最成功的契机……&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:26px;background-color:#FFFFFF;\">　　成东青请王阳加入一起办补习班，成东青毋忘孟晓骏，让他回国强势加盟，正式开办\"新梦想\"学校。三人凭借个人魅力，包括成东青的自嘲式幽默教学法，孟晓骏的美国经验和签证技巧，以及王阳的创新电影教学，让新梦想空前成功。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:26px;background-color:#FFFFFF;\">　　新梦想再扩规模，成东青被媒体和青年塑造成为留学教父，不由自控的散发着一股从土鳖蜕变成领导者的光芒，让孟晓骏看不过去，二人渐貌合神离，王阳左右做人难。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:26px;background-color:#FFFFFF;\">　　孟晓骏远走沈阳，三人的友情面临重重考验。然大时代一幕又一幕的挑战，包括1999年南斯拉夫中国大使馆被北约军机轰炸、还有ETS美国普林斯出版社控告新梦想侵犯版权，又把三子再次凝聚起来，共同面对新梦想的困境。&nbsp;</span><br />\r\n<p>\r\n	<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:26px;background-color:#FFFFFF;\">　　这三个非一般的中国人，是大时代下最具代表性，最激励人心的\"中国合伙人\"。&nbsp;</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-family:SimSun;font-size:14px;line-height:26px;background-color:#FFFFFF;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src=\"http://img1.gtimg.com/ent/pics/hv1/199/32/1318/85711309.jpg\" alt=\"《中国合伙人》曝3B青年特辑 致终将牛B的青春\" /><br />\r\n</span>\r\n</p>','我是好人',29,'1496.jpg',237,'','2013-05-18 01:49:57',NULL,0),(1494,'潘小莲酸奶 传说中美味','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">很温情的一家店铺因为背后有着如此可爱的故事，为女儿开的一家酸奶店铺，再以她的名字命名，店里没有太多关于店铺来由的介绍，但经过亲身体会以及品尝了招 牌酸奶以后，我觉得那浓浓的就像是父母对女儿的爱，并在岁月中一家人不断成长的，其中慢慢融入了更多的元素，如同才开店的时候只有原味酸奶，现在有了招牌的芒果和新的招牌——榛子酸奶。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 店里的装修让人犹如置身欧式风格的画廊一般，个色油画整齐有序的排列在青绿色的墙边，精致的画框衬显这一幅幅虽不是名画但别有一番韵味的作品。就连「潘小莲酸奶店」的招牌也经过老板精心设计与</span><a href=\"http://www.manshijian.com/articles/article_search/5raC6bim.html\"><span style=\"font-family:SimSun;\">涂鸦</span></a><span style=\"font-family:SimSun;\">，和复古的藤蔓式的吊灯互相回应着，看着店铺每天来来往往的人潮，曾经来过的走了的要来的每一位客人。店中的一切显得那么的活泼与可爱，也许这就是老板眼中的女儿，一个天真活泼如春天一般的小女孩。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 店里除了招牌的各式以外还有芒果的，不会很甜或很腻总是让人觉得有种很厚实的质感。店里做工的服务员们都很热情，身着店铺特色格子衫外面穿着店家招牌围裙，不时的总会变成合影留念的模特。店铺的墙的空白处也专门为来到「潘小莲酸奶店」的朋友们留下一 处空地可以放上到此一游的照片，可立拍的相纸层叠着一幅幅灿烂的笑容。</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1506/20130516172258_18772.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1506/20130516172310_88537.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1506/20130516172319_37640.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1506/20130516172352_55132.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1506/20130516172408_51097.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1506/20130516172427_78660.jpg\" /><span style=\"font-family:SimSun;\"></span>\r\n</p>','我是好人',25,'1494.jpg',258,'','2013-05-18 01:43:52',NULL,0),(1497,'崔健：并不是一无所有','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1391/20130511195320_91354.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp; &nbsp; &nbsp;傍晚时候我实在无聊就出去校门口随便逛逛。途中见到一胖子穿着裤衩趿拉着拖鞋晃晃悠悠迎面走来，一路走一边摇晃着肥硕的肚子甩着膀子嘴里还嘟嘟囔囔着什么，走近了才听清楚他在唱“我曾经问个不休/你何时跟我走”之类的东西。这是崔健的东西，好久没有听人唱了。在即将错肩而过的时候我叫住了他，说，哥们儿，唱歌呢。他拖鞋往地上一顿，肚子也戛然而止，说，是啊，咋的了？我涎着脸皮笑着说，没咋。是崔健的歌啊。他说，嗯，是啊，你也喜欢？我说，还行。他表情作兴奋状，咱聊聊？我说，不了，我现在太无聊，不想聊这个。他表现出一副理解的神情，说，嗯！是这样。那你先逛。于是，我在他的“我曾经问个不休/你何时跟我走”的背景</span><a href=\"http://www.manshijian.com/articles/category/yinyue.html\"><span style=\"font-family:SimSun;\">音乐</span></a><span style=\"font-family:SimSun;\">声中也晃晃悠悠出去了。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; 我可以想想这哥们儿日常生活的状态，每天凌晨睡觉，十点钟起来，开始查资料写论文，中午吃饭，下午继续写论文，晚上看部</span><a href=\"http://www.manshijian.com/articles/category/dianying.html\"><span style=\"font-family:SimSun;\">电影</span></a><span style=\"font-family:SimSun;\">打打游戏到十二点算是一天的结束。所有的研究生都在这样一种状态中度过，每个人在精神的空虚之中妄想着以后的富贵安康，而学校和导师也在不停的暗示这一点。如同八十年代我们接受四个现代化教育一样。然而，面对现实的时候，我们常常想表达一种消极情绪，消极情绪的表达需要一种合适的途径，不知道多少届学生过去，每届学生总有人选择一无所有。这是一种文学上的夸张，因为，其实每个人读到研究生，他的未来绝对不会一无所有。然而，大家总是喜欢用这样一种方式将自己的状况表达的更具有情绪化，希望得到一种理解，最终是需要得到一张同情和安慰，来弥平心理上的孤独。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; 由于时间的关系，我现在无法完全理解当年在北京工人体育场上的观众随着崔健的音乐大声吼叫的心情，但是我能理解那位在场的体育总局的领导的鄙夷的态度。他或许有自己的审美观，然而面对世俗的生活，他一时还不能从自己的那种人为强加的审美观中自拔而出，他不能屈尊于当时的世俗的精神生活当中，这是他们的一种悲剧。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; 现在很多歌曲都号称摇滚音乐，很多歌手都号称摇滚歌手。然而，在我眼中，中国的摇滚歌手就那么几个人，崔健和唐朝乐队，再往下数，就不知道谁能算的上了。因为摇滚不止是音乐，他更多的是表现出来的是歌手对于生活的思考，而这种思考的动力是源于社会的责任感。一首歌，从他的诞生的时候或许不能证明歌手的价值，然而时间的流逝人们的某种心情依然不变，依然依赖于某种歌曲才能表达出来，这不就是音乐的价值特别是摇滚音乐的价值所在吗？那位手持仙人掌的胖子和我一样也不是从崔健那个时代走过来的，然而面对某种类似于从前的生活状态对《一无所有》的喜欢不就说明了这一点吗？</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; 这么多年来，崔健一直坚持舞台表演，很少接受媒体的采访，也不参加现在很多歌手乐此不疲的选拔比赛之中，崔健只是陷入自己的生活当中并快乐的将生活视为自己音乐之树的水源。不管发生什么，不管喜怒哀乐，生活总得继续。从这个角度来看，崔健并不是一无所有。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>','我是好人',26,'1497.jpg',275,'','2013-05-18 01:52:04',NULL,0),(1498,'爱，是一本斑斓的画册','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1992/20130517170609_29190.jpg\" /><span style=\"font-family:SimSun;\"></span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp;&nbsp;爱，是一种温暖，一种心痛，一种真实的生命。爱，是一条偏僻的小路，总能通达心灵的深处；爱，是一本斑斓的画册，总有刻骨铭心的风景；爱，是一朵馨香的兰花，总在风雨中静默绽放。爱，不是一种表达而是一种感受；爱，不是一个结果而是一个过程；爱，不是一种承诺，而是一种守候，一种懂得，一种彼此的融化！</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;　 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ————巴山夜雨</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;秋夜静默，月下独酌。风，掀起了心事，把一份月色轻轻揉进心菲，成为一叶干净的小诗；秋，落寞了心情，把一抹风景默默留在岁月，成为一季冬事的留白；月，潮湿了夜色，把一樽清酒缓缓洒在心头，成为一场寒雨的豪迈。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;谁的洞箫在远方低吟，谁的呼唤在痛心彻肺，又是谁的脚步在夜半孤独徘徊，守候无眠，只待清晨的第一红光芒！</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;用心爱生活。生活，是一本渐渐打开的书。纵然有诸多无奈，只要能晨观日出，暮赏霞落，就是一种简单的幸福；纵然有诸多风云，只要能望月守心，清明思绪，就是一种悟了的心境；纵然有诸多篇章，只要细细翻阅，慢慢品味，那细节那美丽那情怀，就是一种明白，一种感动，一种震撼。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;其实，世界很美，大自然更美，我们自己的生活最美！</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;用心爱文字。文字，是绽放在指尖上的一朵小花。它，干干净净，来自美丽的心田，它，清清爽爽，来自淡然的心境；它，洋洋洒洒，来自大自然的感悟，来自人生的况味，来自生命的震撼。文字是一汪清泉，涓涓流淌，总浅释伤感纯澈心扉；文字是一节短笛，悠悠飞扬，总沧桑尘世涤荡灵魂；文字是无声的心语，总凝结思想，升华自我！</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;用心爱人生。人生，就是一个大舞台。不该面对的，应该面对的，都要面对，这是一种灵魂的洗礼一种心境的纯澈；不属于你的，属于你的，都要争取，这是一种生存的法则一种生命的搏击；不该放弃的，该放弃的，都要珍惜，这是对他人的尊重也是对自己的尊重。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;天高鸟飞，海阔鱼跃，这就是胆量，这就是气魄，这就是大美的人生！</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;每个人都有一个属于自己的空间。空间，是一面美丽的镜子。喜欢默默品味文字，如风般潇洒如雨般纯美，如心般的通透清爽；喜欢静静搜集图片，如花般芬芳如水般清凉，如梦般的令人神往；喜欢漫漫聆听</span><a href=\"http://www.manshijian.com/articles/category/yinyue.html\"><span style=\"font-family:SimSun;\">音乐</span></a><span style=\"font-family:SimSun;\">，让时间如诗般灵动让岁月如词般婉约，让生命如曲般唯美。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;每个人，都应有一个属于自己心灵的地方。不求奢侈只要简单，能放下一颗安静的心；不求繁华只要静谧，能装下一片美丽的月色；不求感动只要懂得，能守候一生无怨无悔的缘分。不需太多言语，一回首就在彼此视线里；不需太多承诺，一声轻唤就在彼此泪光里；不需更多更美更深的表白，一个微笑就在彼此生命里！</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;说，一直是别人，做，一直是自己！用心生活，干干净净做人，坦坦荡荡做事，清清爽爽来去！</span>\r\n</p>','我是好人',28,'1498.jpg',264,'','2013-05-18 01:53:50',NULL,0),(1499,'叠人塔——造罗汉','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">在我们看来叠罗汉不过，其实叠罗汉更是是一种技艺。虽然一般都看做是体育活动或表演，构成形式由二人以上的人层层叠成各种样式。人上架人，重叠成各种形式，是体操、杂技表演项目之一。一般常出现于马戏团特技表演、啦啦队表演以及舞蹈表演等之中。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　叠罗汉，在我国民间，也是一种传统的体育游戏。由若干人互相配合，组成造型动作。叠罗汉多层盘腿而坐的罗汉组成造型，故名“叠罗汉”，以后逐渐发展演变为多种技巧造型。分为男子、女子和男女混合造型几种。简单的叠罗汉对参加者的身体素质和技术水平要求不高，也不受场地条件的限制，适合在广大城乡中、小学中开展。参加练习者必须密切配合协作，因此对培养青少年团结互助的集体主义精神有良好作用。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　在国外，西班牙的加泰罗尼亚地区更是将叠罗汉视为民族体育活动，传说古代在与法国战争时，曾以此法扔炸弹至敌方城墙内，为纪念此举，因此一直是当地重要的表演活动。西班牙的自治区「加泰罗尼亚」把「叠罗汉大赛」(Tarragona Castells Comptetion)当作重要的民族体育活动，竞赛当天时时可见用人群堆叠的「人塔」，极度震撼!活动的来源可以追朔回18世纪，有一说是因为早期与法国打仗时，当地人用叠罗汉的方式扔炸弹到敌方城墙内，後人为了要纪念这项行动，就衍伸出叠罗汉的竞技活动，而这项传统也在2010年被联合国列为人类历史中的重要遗产!</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;起源于18世纪末的叠人塔是西班牙加泰罗尼亚地区最具特色的传统活动之一，一座人塔少则六七层，最高可达到十层，真可谓是西班牙版的叠罗汉。“力量、沉着、勇敢、理智”是形容这项活动最好的四个词汇：下层的成年男子需要托起数人的重量并保持稳定，一个小小的晃动可能就导致功亏一篑;任何突发状况都需要参与者沉着应对，化险为夷;爬上高高塔顶的三四岁小朋友便是勇敢的最佳代言人;每次人塔搭建都要经过队员们的一番精心策划,甚至还会绘制准确的位置图。叠人塔常见于各种节日庆典中,即使只是一个社区的小节日，加泰罗尼亚人民也会在小广场上自娱自乐一下。在众多节日中，吸引众多参赛队伍的莫过于是巴塞罗那9月24日的梅塞节。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp;&nbsp;人塔的命名一般根据每层的人数以及总共的层数来决定，有时候也会根据塔的构成形式来命名。一般来说，除基层外，每层的叠塔人数为1~5人，而最常见的人塔层数为6~10层。叠塔过程中，无论在一旁欢呼鼓舞的观众，还是积极参与其中的搭建人员，热情高涨、欢欣雀跃，通过团结合作，堆叠出一座昂然壮观的人塔，显示出一种超越种族、宗教、年龄和性别的魅力。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130416124623_22104.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130416125236_70513.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130416124624_59305.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130416125317_37457.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130416124626_90119.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130416124631_82997.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130416130008_85398.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130416130049_87478.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130416124633_69131.jpg\" />\r\n</p>','我是好人',30,'1499.jpg',249,'','2013-05-18 01:55:48',NULL,0),(1513,'择一城终老，遇一人白首','<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">时光静好，与君语；细水长流，与君同；繁华落尽，与君老。想来这一生，总会有那么一个人，牵着你的手，将爱</span><span style=\"font-family:SimSun;font-size:14px;\">融入生命</span><span style=\"font-family:SimSun;font-size:14px;\">，倾一世温柔，与你一起待霜染白发，陪你看细水长流。　　　　　　——题记</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<img border=\"0\" alt=\"择一城终老，遇一人白首\" align=\"right\" src=\"http://www.duwenzhang.com/upimg/130604/1_102515.jpg\" width=\"179\" height=\"140\" /><span style=\"font-family:SimSun;font-size:14px;\">　　岁月荏苒，浅夏将一种极致的婉约，律动在岁月的眸里，我轻倚季节的转角处，安然于这份静好。轻轻浅浅的日子，散发着恬淡的香，那是时光沉淀的馨香。一直认为，最长久的幸福，是来自平淡的日子；来自宁静的心境；来自平凡日子里点点滴滴滴的感悟。繁华落尽终究是平淡，生活的美，不在于绚丽，而在于平和；爱情的美，不在于轰轰烈烈，而在于平凡的相守，温暖的陪伴。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　年少的时候，曾对爱情有过很多幻想，那么渴望能成为爱情童话中的主人公，期待白马王子有一天能穿越千山万水来到身边，牵着我的手走向幸福。然，年华如水时光辗转，王子终究没来，我也没有穿上灰姑娘的那双水晶鞋，在似水流年里将一颗心安放在岁月中，拥有了一份平淡的爱情。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　“择一城终老，遇一人白首”，不知在哪里看到这句话，不由得心生感动，想来这一生，总会有那么一个人，牵着你的手，将爱融入生命，倾一世温柔，与你一起待霜染白发，陪你看细水长流。多少人间烟火，在细细碎碎的时光里静静氤氲；多少沧桑坎坷，在身手相牵的岁月中远去，任容颜在日复一日的平淡中慢慢变老，花虽落，风住尘香；水长流，云淡过往，惟不变的是彼此旧时的欢颜。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　也许生命的美在于遇见，我不知道这一生会遇到多少人，也不知道会有多少倾心的相遇，或许这世上有很多人都可以惊艳你的时光，但能够愿意留在你身边直到慢慢温柔了你的岁月，陪你哭，陪你笑，陪你等待，陪你花开，一生也许只有那么一个。惊鸿一瞥是生命的美妙，细水长流才是最真的幸福。佛说前生五百年的回眸，才换得今生的一次擦肩，那么于千万人之中遇见了你，牵手一生一世，又是怎样的一种修行呢！</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　或许你不是最优秀的，但一定是最懂得心疼我的。累的时候，你的肩膀可以让我依靠；寒冷的日子，你的怀抱就是我的温暖；伤心的时候可以得到你的安慰；寂寞的时候有你的微笑陪伴；快乐的时候可以与你一起分享；风风雨雨我们一起走过；红尘纷扰我们一起面对，任岁月平淡了流年，任时光抹去了激情，你的爱，一直都在。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　或许你不是那么完美，但一定是最懂我的那个人。懂得是心与心的相通；是灵魂与灵魂的相依；懂得是相爱的两个人心中开出的最美的花朵，有的时候，懂比爱更重要。因为懂得，所以理解；因为相惜，所以包容。爱不是不争不吵，是发生争执后还能在一起；爱不是不打不闹，是吵过闹过后依然不离不弃。一份懂得，几多欢喜；一份陪伴，几多温暖，爱无言，千回百转；情无声，寂然欢喜，有你的地方就是我此生最美的风景。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　张爱玲说：我一直在寻找那种感觉，那种在寒冷日子里，牵起一双温暖的手，踏实向前走的感觉。一生一世的牵手，多么温暖，从青春年少到步履蹒跚；从红颜到白发，在彼此默默注视中慢慢变老，还有什么比镌刻着岁月冷暖的这份情更珍贵呢？</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　一直认为最好的爱情，须与时日一起生长。我不羡慕花前月下的卿卿我我；也不羡慕朝朝暮暮的山盟海誓，我羡慕的是百发苍苍夕阳下相依相扶的身影。从人生初识的相看两不厌，到时光将爱情打磨成亲情，牵了手的手，没有岁月可回头，当激情退却，当浪漫殆尽，只有心灵深处的取暖才能绵延。爱是衣带渐宽终不悔的执着，爱是为伊消得人憔悴的不悔；爱是微笑向晚，携手共阑珊，最好的爱情，是给你一生。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　尘世的屋檐下，有多少人就有多少爱恋，有多少风花雪月的缠绵，就有多少幸福和温暖。当你穿行在万家灯火，我会为你点燃照亮你回家路的那盏灯；当你风尘仆仆的奔波回来，我会用温柔的手为你洗去倦容；当皱纹爬上你的额头，我会握紧你的手，陪你一起变老，在琐琐碎碎的的日子里，诠释执子之手，与子偕老的永恒。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　有人说：爱上一座城，是因为城里住着某个人，能够与所爱的人在一起，连光阴都是美的。即便粗茶淡饭，修篱种田，只要有你陪伴就好。那么，找一个青山绿水的地方，寻一处幽静的茅舍，或是云水禅心的庭院，那里有晴朗的阳光和静谧的悠然，还有你明媚的笑脸。掬一捧花香在平淡的日子，握着一路相随的暖意，让爱的馨香在柴米油盐中升腾；在一杯茶的温情里，体味生活的诗意；在一碗粥的清淡中，感受生活的浪漫，每天清晨你和阳光都在，便是我的幸福。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　春暖花开的日子里，我想牵着你的手漫步在通往田野的小径上，蓝天之下，有清风掠过，身后是一排排苍翠的竹林青叶，远处是皑皑的青山。我与你掬一泓泉水的清澈，携一缕清风的洒脱，看蝶飞花舞，聆听花开的声音，尽情享受每一缕阳光的温暖，感受每一滴雨露的滋润，让相惜的暖意在风和日丽中增长，这一刻，我愿放下所有的执念，只想做你手心里的宝，用一朵花开的时间，守望幸福。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　飘雨的日子，我们依偎在一起，临窗而坐，听雨打芭蕉的声音。任光阴在窗外流淌，我沏壶清香四溢的花茶，静静感受那清风与细雨的缠绵，斟一盏岁月沉淀的芳香，细听时光的呓语，回味过往一段段美好的画面，品茗生活给予的点点滴滴，在安静的时光中，淡看流年烟火，细品岁月静好。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　年华向晚，不过岁月沉香。待到老去的那一天，两鬓斑白，步履蹒跚的我们已经不能再走千山踏万水，我和你围在火炉边，在我们的皱纹与白发里，细数光阴的痕迹。感谢生命中的缘分，让我遇见了你，有一种情，永远不老，只为与你相识时的美好；有一种爱，深藏心中，只为与你相爱时的淡然，这一生，最幸福的事，便是牵着你的手一起走过。当尘世烟火慢慢沉寂；当指尖浮华逐渐消散，你依旧如此牵着我的手，岁月老了，情还在，原来我生命中最美的时光，是从遇到你的那一刻开始。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　择一城终老，遇一人白首。</span> \r\n</p>','我是好人',28,'1513.jpg',257,'','2013-06-06 14:35:16',NULL,0),(1500,'致青春致科比：17年似水年华','<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">按照联合国教科文组织的说法，青年的界限是34岁。所以，这个赛季，是科比青年时代的最后一季。青春终将逝去，在青春的尾巴上，科比度过了最艰难的一个赛季。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　在青春的尾巴上</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　去年的休赛期，对于科比来说并不是一个糟糕的夏天。他明白自己的好时光不多了，他想趁着青春犹在，再为总冠军奋力一搏。当他在伦敦奥运会为国征战的时候，湖人总经理库普切克帮他张罗好了一切——一个可靠的组织者纳什、一个斗志旺盛的老将贾米森、联盟中最好的中锋德怀特·霍华德。剩下的事情看起来是那么简单，在不少人看来，总冠军几乎已成科比的囊中之物。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　科比自己的状态也保持得不错。新赛季首堂训练课上，面对拿着训练营合同、拼命防守的克里斯·道格拉斯-罗伯茨，科比轻松完成一记隔人暴扣。“休赛期，我打了奥运会，所以很好地保持了体型和状态。”科比说。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　在纳什因伤休战的时候，科比展现出了职业生涯前16年中很少出现的一面。2012-13赛季的第4场比赛开始，科比有意识地扮演起组织者的角色。连续的8场比赛中，科比场均送出7次助攻，这8场比赛湖人6胜2负。科比信誓旦旦地说：“这样的状态我可以保持整个赛季。而且当纳什回来之后，我会更轻松。”2013年1月底的3场比赛中，科比再次展现自己组织进攻方面的才能，连续助攻上双，率队取得3连胜。苦中作乐的科比开起玩笑，“我很全面的，我可以做很多事情。如果我专注于传球，我能拿下助攻王。”</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　在青春的尾巴上，科比似乎没有老去。他的上场时间与上赛季基本持平，投篮命中率、三分球命中率和助攻都有不小的上升。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　3月11日，道格·里弗斯看了湖人与黄蜂的比赛。那时候，湖人正处于季后赛边缘，科比在那场比赛砍下42分、12次助攻。里弗斯赞道：“我从没见过一个超级球星这么打球，就好像是一个必须拼命的角色球员。”4月10日，对阵开拓者的比赛，科比打了48分钟，砍下47分。德安东尼说：“无论他的身体，还是他的心态，或者说他的一切，都处于非常年轻的状态之中。”</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　很遗憾，德安东尼错了。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　眼泪，祭奠青春</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　4月13日，距离季后赛开打只有5天的时候，科比的赛季结束了。当湖人全力冲击季后赛的时候，谁都没想到剧情会发生如此戏剧性的转变。就连科比自己也不愿意相信，当他受伤的那一刻剧痛无比时，还转过头去询问哈里森·巴恩斯，“你是不是踢到我的脚了？”可惜他得到了否定的回答。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　赛后，科比的脸上挂着泪珠，德安东尼以手掩面欲哭无泪。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　尽管有消息称，科比的脚伤与疲劳关系不大，不是科比受伤的主因。但不可否认的是，上场时间过多，状态出色导致的严密盯防，间接促成了这次伤病的发生。科比本赛季的最后7场比赛，上场时间全部超过41分钟，其中3场超过47分钟。受伤的那场比赛，科比离场之时，也已经打了45分钟。整个4月份，他的场均上场时间高达45.2分钟，17个赛季以来，科比从未承受过这样的强度。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　不过，对于德安东尼所遭到的指责，科比主动站出来为他开脱。“那都是我自己的主意，你们不必责怪德安东尼了。”科比说，“在我之前，‘魔术师’约翰逊和迈克尔·乔丹也都是自己决定上场时间，过去，菲尔·杰克逊执教的时候，我同样是这么做的。”</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　科比还开起玩笑，“你们觉得德安东尼真的能决定让我什么时候上场或者下场吗？”</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　青春，莫谈辜负</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　也许科比的这个赛季注定是一场毫无收获的旅程。2012年夏天，球队的积极运作，如今看来，埋藏着无数的隐患。纳什已经39岁，防守能力平平，体力也是个很大的问题。霍华德的伤病是更大的隐患，尽管本赛季只缺阵6场，但状态大不如前。另外，球队中也缺少一名能够撕开对手防线的突破型球员。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　更重要的是，球队的磨合远比想象中艰难。科比是球场上的实干家，在场下，他并不是一名擅长捏合不同性格球员的领袖。面对甘愿牺牲的纳什、渴求胜利却得不到机会的贾米森、要求更多球权的霍华德、偏执的德安东尼，科比无能为力。老巴斯的离开，更是令球队变得一团糟。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　正如贾米森在赛季结束后所言，“整个赛季，这支球队根本没有产生化学反应。”</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　然而，选择了湖人不只是选择机遇，更是直面挑战，任何人都不可能把这个赛季的失败栽赃到科比头上。就像2011年在总决赛输球的热火，不能把责任全都推给詹姆斯。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　尤其对于霍华德来说，他应该明白，尽管洛杉矶是科比口中“最适合中锋打球的地方”，但前提是，他必须适应科比。而且，在赛季中后期，科比已经做出了很大程度上的牺牲和妥协，在一些比赛中，他与霍华德的配合甚至显得非常刻意。而在赛季结束之后，科比还表示会邀请霍华德到家里做客，并坦言想说服霍华德与湖人续约。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　因此，对于这支湖人来说，没有哪名球员需要背负全部责任，也没人有资格谈“辜负”。从来到这里的那一刻起，他们就应该明白，这注定是一个机遇与风险并存的赛季。就像科比那样，他拼尽了全力，但没有把怨气发泄到德安东尼身上。为了出场时间的争论，他力挺德安东尼；当德安东尼称不停发推特的科比只是一个“球迷”时，科比立即闭嘴。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　如果这都不算爱</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　对科比来说，最可贵的是患难与共，相知相伴。在这个时候，他最挂怀的还是保罗·加索尔。“我们之间有兄弟间的情感。”科比说。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　本赛季，加索尔将被交易离队的传言不绝于耳，如果不是科比在背后的默默支持，加索尔恐怕早就呆不下去了。两人在推特上互动，相互鼓励支持。在加索尔伤愈复出后状态不佳时，科比直斥批评者的无知，“说加索尔打得不好的人，都不懂篮球！”</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　常规赛最后一战击败火箭、锁定东部第七之后，科比在推特上说：“交易加索尔的声音能停止了吗？”那一战，加索尔砍下17分、20个篮板、11次助攻，力保湖人顺利收官。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　赛季结束后的新闻发布会上，总经理库普切克摆明了他的态度。“我们有信心围绕霍华德建队。”库普切克说，“当你输了球，所有人都会进入交易计划之中，加索尔也不例外。”科比也表明了自己的态度，“根本不用考虑，我们需要加索尔。只有留下他，我们才能有更大的夺冠机会。”</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　科比希望霍华德留下，但他更看重与加索尔多年并肩战斗建立起来的情谊。就像季后赛最后一场，在最需要安慰的时候，科比就在加索尔的身后。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　“我最喜欢那个支持我的科比。”加索尔说。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　尾声</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　还有十多支NBA球队正在征战季后赛的时候，科比已经迫不及待地放出下赛季的豪言，“保持健康，我们可以夺冠。”</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　而在这个时候，这支湖人似乎还有一些事情需要解决。贾米森的合同到期了，他进行了手术，没有参加赛季结束后的告别发布会。在此前的一次采访中，贾米森表示，愿意去离家更近的山猫打球，似乎已经无意再争总冠军。纳什表示，这是他职业生涯中最失望的一个赛季。霍华德穿着一件卡通T恤，仍然像个孩子一样爽朗地笑着。慈世平沉迷推特，口无遮拦，有人建议TNT解雇奥尼尔、聘请慈世平。另外一些没有合同在身的角色球员，莫里斯、伊班克斯、克拉克、高德洛克、萨克雷，各怀鬼胎地向球队示好。还有备受争议的德安东尼，刚刚在距离湖人训练馆10分钟车程的地方花690万美元购置了一套豪宅，他还说下赛季球队将延续今年4月份的打法。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　唯一令他们感到轻松的也许是，去年夏天被交易离队、本赛季一场未打的拜纳姆，像个白痴一样在西班牙热舞……</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">　　一切貌似烟消云散，又好像暗流涌动。</span> \r\n</p>\r\n<p style=\"font-size:14px;font-family:宋体, simsun, sans-serif, Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"http://img1.gtimg.com/sports/pics/hv1/102/143/1315/85544442.jpg\" alt=\"特评：湖人再见科比再见 致终将逝去的青春\" /><br />\r\n</span> \r\n</p>','我是好人',30,'2000.jpg',315,'','2013-05-20 23:38:51',NULL,0),(1506,'泰国曼谷自助记','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">旅行，是一种生活方式。走的路越多，越会知道自己的无知和狭隘。更多的时候，我希望用旅行去近距离观察同在这个星球上的另外一群人，去体验他们和我不一样的生活。传统的随团旅游显然无法满足我的需求，这就注定我只能选择自由的行走。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 也许是受朱兆瑞3000美金环游世界的经济旅行学影响，在尽量满足便捷和舒适的前提下，我倾向做一个Backpacker，崇尚节俭的原则，拒绝FB。临近岁末，这一次我选择把自己独自放逐到一个完全陌生的国度，也许爱情的结果对我来说更像是宿命，但最起码在离上海3000公里以外的那片土地我可以自由自 在的活着。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;</span><strong><span style=\"font-family:SimSun;\">遭遇曼谷</span></strong><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 飞机是在傍晚时分到的曼谷Don Muang国际机场。我在曼谷订的酒店是在背包族里小有名气的SUK11 Hostel。在机场出口处就有机场巴士可以前往（AB3线），100Baht（按美金汇率折算，1元人民币=4.7Baht)到BTS的Nana Station站下，一般司机到站会提示。依照SUK11网站上提供的地图，尽管它的入口很不明显，但还是比较容易的就找到了。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 房间不大，也没有电视，但是很干净，相对于它450Baht每个晚上（含</span><span style=\"font-family:SimSun;\">早餐</span><span style=\"font-family:SimSun;\">）的价格还算是物有所值。住的人大多是西方人，亚裔面孔的很少，它的一楼和二楼都有Common Room，里面有电视，DVD，图书馆等，最棒的它提供了很多有用的信息，比如去大皇宫的路线，去Ayuttaya的火车时刻表，去Pattaya的汽车 时刻等等，这为我接下来的旅行提供了很多方便。</span> \r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1638/20130521151433_50894.jpg\" /> \r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 曼谷的时间比上海晚一个小时，早上醒来的时候看看时间还早，索性换上一身短打早些出发。如果一定要区分曼谷的季节的话，一年中曼谷有三个季节，夏季 (Hot)，潮湿的夏季(Hot and Humid)，雨季(Hot and Wet)。现在正是夏季，是一年中最好的季节了(11月到2月)，阳光极其充沛，还有丝丝微风，是旅游的黄金季节。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我乘上BTS Skytrain（轻轨）换渡轮前往大皇宫，轻轨的票价从10Baht到40Baht不等，就其相对路程来说，比上海稍贵，但比香港要便宜得多了。此时正是上班高峰，但车厢里并不挤，到处都是打扮入时的白领，曼谷女性普遍皮肤偏黑，可能是光照的原因吧，有点类似中国的广西、海南，但是眉眼间很有神韵，轮廓很清晰，从审美的角度来说，颇有一些美女。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 从码头上岸，我信步前往大皇宫，迎面过来了一个中年男子，看我拿着地图的样子，便很热心的上前解说，并介绍最近曼谷的几项展出活动，还告诉我乘车线路，我正要道谢时，他叫来了一个嘟嘟车司机，并帮我还价到30Baht，我当时有点盛情难却，而且想想时间还早，就上了车，谁知刚让我逛完第一个景点后这个司机就开始推荐我去购物，在我几次婉言谢绝后，便开始发牢骚，这时我正好注意到地图上的提示，如果有嘟嘟车司机强行推荐购物请拒绝并拨打监督电话云云。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我要求下车并把这行提示给司机看，他无奈之下阴沉着脸把我放下。待我打车回到大皇宫时，门口已经，所以我并没有久留在这在曼谷唯一可能可以用中文沟通的地方。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在回SUK11的路上，我在比较曼谷和中国的城市，从感觉上来说，曼谷有点像是中国90年代初的广州，车堵得让人难以想象。但曼谷对于外国人设想得很周到，所有的车站，道路，餐馆都有英文标识，在曼谷这一整天我基本没有遇到任何我事先设想过的沟通上的困难，这一点中国的城市可能还有差距。另外，我所遇到的曼谷人大多极其友善，有些人可能是因为黑看上去很凶恶，其实心里很热情很和气，自觉不自觉都在维护着他们城市的形象。游客如织了。我想起以前好像也有驴友提到过同样的遭遇，望后来人切记在大皇宫附近不要相信陌生人的热情推荐。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 和中国的故宫还有佛教寺庙相比，泰国的皇宫和玉佛寺没有那么大气，但是在细节上，感觉更加考究，它的建筑在色彩的处理上非常和谐，在金色和红色之外用了很多蓝色和绿色，让人觉得精致而且不容易产生视觉上的疲劳。玉佛寺周围的墙上有很多精美的壁画，是讲述泰国佛教的传说，可惜我没有这方面的知识，只能稍稍略过。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 从大皇宫出来，经过一片大的绿地，就可以去著名的Khao San Road。这片绿地附近的区域应该是曼谷的政治中心，可以看到很多泰国现国王的肖像，可能这是他们表达对国王爱戴的一种方式吧。Khao San Road其实是短短的一条小街，上面都是小摊小贩，餐馆和旅游服务中心，从这里可以很方便的拿到签证去柬埔寨、老挝、越南、尼泊尔、印度，还有中国。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 顺便也去逛了逛曼谷的唐人街，唐人街在火车站附近，据说当年泰国的郑王朝被推翻后，新国王为了限制华人，把离大皇宫最远的一块地方选为华人聚集区，这就是现在的唐人街，我到的时候已是华灯初上，满街都是卖燕窝鱼翅的，有兴趣的可以去试试。感觉上很像香港的旺角一带，而且我有一种直觉，这里可能10年前甚至 20年前就是这样，基本没什么变化，这种不变可能也包括生活方式，我并没有这种守旧情结。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ayuttaya是泰国的古都，又称大城，在曼谷北部大概76公里，它在Ayuttaya王朝和缅甸王朝作战中被毁，城里保留有大量珍贵的遗迹，多处世界文化遗产，那个经常被作为泰国古迹的标志——藏在树里的佛的头像就在Ayuttaya。这样的地方我自然是不会错过的。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我在比较了几种去Ayuttaya的方式后选择了乘火车前往，还是那几个优点，快捷（想想汽车堵在高架上一动不动的感觉吧），方便（可以乘地铁直接前往火车站），经济（果不其然，从曼谷到Ayuttaya的Express Train只要40Baht），还有最后一项，可以体验一下在异国乘火车的感觉，这种经历也是极其难得的。可能因为没有中国那样人口对交通的压力，乘火车的人不多，很多是欧美的背包族，而且大部分站没有围墙可以随便进入，自觉购票，火车停了自己上车，更像是国内的公共汽车。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 车行大概一个小时即到了古城，古城虽然不大，但是景点多且分散，完全靠步行可能不行，在这种情况下租自行车是个不错的选择。出了火车站对面就有租自行车的，30Baht一天，只需要简单的登记一下就可以领车和地图上路了。这时其实应该了解清楚一下方位，并问清楚基本路线，因为后来我发现在古城里头用英文问路基本是一件Mission Impossible。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我</span><span style=\"font-family:SimSun;\">骑行</span><span style=\"font-family:SimSun;\">不久就遇到困惑，按地图上的标识这里应该有一条小路过河，但我看到的是一段高架的上轧道，依照国内的经验，我犹豫再三也不敢骑着自行车上高架，于是 路边一个老伯问路，幸运的是他很热心肠，不幸的是他只会泰语，在对着地图比划了半天后，我按照他指的方向骑行一段后发现还是错的，我后来明白他其实可能根本没明白我要去哪里。绕了一个圈后又回到高架路口，硬着头皮向上骑，果然这回是对的。只是在汹涌的车流中骑车过高架桥也需要一点胆量和技术。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 感觉缕缕的微风拂过脸庞，沿着公路缓慢的骑行，便开始看到许多古迹，大多都已经是残垣断壁了，但依然不失其当年的雄伟。Wat Phra Si Sanplet是其中一处主要的景点，这里曾经是Ayuttaya王朝的皇宫，现在这里的三座佛塔供奉着三代Ayuttaya王朝国王的骨灰，从佛塔的外形上看似乎有让被供奉者肉体重生的意思，不知道在佛教或者印度教里是不是这样解释的。那个藏在树里的佛的头像是在Wat Man That，现在这里已经长满青苔，但仍然可以想见当年的辉煌。</span> \r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 这一天我是到晚上才回的曼谷，但总共的花费只有150Baht（不包括吃饭）。去的火车40Baht+回来的火车20Baht（慢车）+租自行车 30Baht+两个需要门票的寺庙Wat Phra Si Sanplet和Wat Man That各30Baht。我去逛过的其他十几处遗迹都是免费的。折算成美金还不到4块钱，在国内可能连一个景点的门票都不够，我却轻轻松松的从曼谷到大城打了一个来回，实在是超值。当然如果有时间也可以选择骑大象逛寺庙，遥想一下当年的安娜与国王的故事，也是一件很惬意的事。&nbsp; 　　</span> \r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\"font-family:SimSun;\">Bad Guys go to Pattaya</span></strong> \r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 不知是不是调侃，Pattaya最著名的口号竟然是“Good Guys go to Heaven, Bad Guys go to Pattaya”，我自问绝非圣人，既然来了，有些东西也不需要去刻意避让。</span> \r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1638/20130521151523_39792.jpg\" /> \r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我是中午时间到的Pattaya，第一印象，满街都是光着上身的西方男子，看来越南战争以后，他们真的把这里当作夏威夷了。由于是圣诞假期的缘故，很多酒店都已客满，我委托的Hotel2Thailand网站最后订到的是一个度假村，一个人一栋两层别墅，楼下是会客厅，楼上是卧室，屋前就是游泳池，这样的四星级酒店一个晚上才1100Baht。就这样我在不知情的情况下彻底腐败了一次。当然它的缺点就是离海滩有点距离，步行需要15分钟。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 看Pattaya指南里介绍，这里原来是一个小渔村，只是因为越南战争时美军驻扎在这里的缘故，才逐渐发展成现在这样的，它号称有关娱乐你所能想到的一切在这里都可以找到，而且价格低廉。很多欧美的中老年男子趋之若鹜，在此地长期驻扎，租房，租车，租泰妹，结婚没结婚，有些都有了第二代甚至第三代。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 还有一些每年都要来好几次，我遇到的一位长得狂象梅尔吉布森的以色列“老爹”这回就带了几个大行李箱准备在这度几个星期的“长假”。像我这样孤身前往却无意寻花问柳者可能要算异类，每次早上去前台领早餐券时他们得知我又是一个人时都用惊讶的眼神看着我，我不知道该怎么解释，只好苦笑。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 夜幕降临，Pattaya立刻变成了一个灯红酒绿的海洋。我随意的走在以Strip闻名的步行街上，这里到处是穿着暴露的女郎和向游客兜售色情活动的人，可能是由于很多中国旅行团的缘故，有些Bar开始出现了中文标识。当我试图进入一家有中文标识的Go-go Bar时，侍者居然问我从哪个国家来，当我答之从中国来时，他居然要求最低消费为1000Baht，我愤怒的加以拒绝并离开。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 算起来这都是国内低价泰国游团惹的祸，有些可能是中国团定点酒吧，他们都是要给旅行社回扣的，在无法分辨是否随团游客的时候，他只好只要中国人就提高价格。随后我走进了另一家以西方人为主的Bar，其实和曼谷Patpong地区的Bar基本相似，50Baht左右一瓶啤酒，可以听着</span><a href=\"http://www.manshijian.com/articles/category/yinyue.html\"><span style=\"font-family:SimSun;\">音乐</span></a><span style=\"font-family:SimSun;\">看着表演。步行街上也有所谓的Show Case，同样在一个以西方游客为主的场所，看了一场show，无非是用那个器官射飞镖吞鸡蛋吞刀片什么的，更多的时候我觉得她们更像是在熟练的挥舞着一 件兵器，我甚至感觉不到色情的成分，有的只是从惊讶厌烦到无聊的过程。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 临近午夜，我走到室外，此时步行街上人声鼎沸，大批的西方人在倒数计时，当数到零的时候，海滩上开始放五彩的焰火。我对自己说了一声“Merry Christmas”，不知道她此刻身在何方在做些什么，只是希望她平安快乐。在没有我的世界里，愿她能真正找到她要的幸福。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\"font-family:SimSun;\">天堂的颜色</span></strong><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; “当我们开始回忆的时候，就是我们开始苍老的时候。”——杜拉斯</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Koh Larn（琅阁岛）的Camet Beach是一条新月形状的海滩，面朝着印度洋。当我跟随着一群西方游客坐着双排车到达这里的那一刻，我突然有一种眩晕感，那种彻底的美瞬间穿过我的大脑直接击中我的心脏。那种纯净的蓝和绿我记得只有在介绍马尔代夫的图片里曾经见到过，现在它就在我的眼前，如果真的有天堂的话，我相信它就是这种颜色的。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我静静的躺在沙滩上，任阳光晒遍我的全身，大脑似乎已经停止转动，那些平日里的权力斗争发展目标生活琐碎此刻都像是和我完全无关。我只愿意就这么静静的躺着享受纯粹的海滩生活，渴了来一瓶冰啤酒，饿了吃一点海鲜，醒了就在这无边清澈的海水里游一个来回。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我望着沿山而建的木屋，心里在想，也许真有一天，我可以放下上海的一切，到这样的一个海边小岛来度过余生，就像“肖申克的救赎”里的Andy，微笑着跨越无尽的困苦，到达了梦想中那片没有记忆的海洋。在那里，和我爱的人，过那种纯粹生活，任岁月无声无息的掠过，直到白发苍苍。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style=\"font-family:SimSun;\">尾声</span></strong><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 就在我离开海边前往曼谷的半个小时后，印度洋的苏门答腊岛附近发生8.9级强烈地震，其引发的海啸横扫印度洋沿岸各国，已经造成了两万多人丧生，其中包括 大量在泰国海滨度假的外国游客。我知道这是上天在眷顾我让我好好活着，我一定会的，也希望那些在灾难中丧生的人们在天堂里可以过得好。</span> \r\n</p>','我是好人',27,'1506.jpg',245,'','2013-05-26 12:06:22',NULL,0),(1507,'神秘藏乡，康巴高原最惊艳的春色','<p style=\"text-align:center;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520164915_74770.jpg\" />\r\n	</p>\r\n	<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n		<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中路，藏语意为“人和神向往的地方”，距丹巴县城9公里，面朝墨尔多神山，是看古碉和藏寨的绝美地方。和秀美的甲居所不同的是，中路更有威严的碉楼相伴。那些碉楼，几百年的，四角的，五角，甚至更多角的建筑依然散发出威严的锐气，似乎仍然护卫散落四周的民房。</span>\r\n	</p>\r\n	<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165134_80439.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165143_93542.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165154_61585.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165205_50230.jpg\" /><span style=\"font-family:SimSun;\"></span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165208_65268.jpg\" />\r\n	</p>\r\n	<p align=\"left\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n		<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 凡是到过中路乡的人无不为她的嘉绒文化、朴实民风、自然和谐所感动，无不为这里的藏居、古碉、景观而惊叹叫绝。阳春三月，我们走进了中路的乡间田野，那种久违的，在儿时记忆中才有的泥土和家畜的芬芳沁入心脾。康巴高原的藏乡处处绿意盎然，开满了白花的梨树和神秘藏寨的交相辉映，扑面而来的春风构成了一幅幅的春之声画卷。</span>\r\n	</p>\r\n	<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165318_65266.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165328_39362.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165332_56712.jpg\" />\r\n	</p>\r\n	<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n		<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中路乡坐落于一个神圣并风景优美的区域，为数众多的碉楼、宗教建筑及村寨一起构成了一个显著的文化景观。那天清晨天还没完全亮我们就起床准备上山，早晨清新与宁静，空气中那种康巴高原独特的春天味道让人难忘。因体力原因，我请了一个质朴的藏家汉子帮我背包和带路上山。当第一缕阳光洒进山谷时，我们已在半山坡上了。</span>\r\n	</p>\r\n	<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<br />\r\n<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165437_72357.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165447_18286.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165459_18439.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165506_16462.jpg\" />\r\n	</p>\r\n	<p align=\"left\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n		<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp; &nbsp;从山上登高望远，只见大山的怀抱里深藏了这些原生态而美丽的村庄，俯瞰整个嘉绒峡谷及周边的藏族村寨，景色优美。可以清晰的看见默尔多神山，藏居，古碉楼被大片大片的绿色包围着。在墨尔多神山下，翡翠般的青草绿树之中，黄、黑、白相间的碉楼寨房隐隐约约分布其间，这是个宁静如诗般的美丽田园，迷人的光影在山坡上缓慢地流动，照亮了村庄田野和碉楼。恍忽间竟有一种踏入仙境的感觉了。</span>\r\n	</p>\r\n	<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165649_65364.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165659_61860.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165709_27656.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1640/20130520165719_74659.jpg\" />\r\n	</p>\r\n<span style=\"font-family:SimSun;\"></span>\r\n</p>','我是好人',27,'1507.jpg',264,'','2013-05-26 12:13:39',NULL,0),(1510,'我与香港电影','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-family:SimSun;\">《文周》：你是从什么时候开始接触香港</span><span style=\"font-family:SimSun;\">电影</span><span style=\"font-family:SimSun;\">的？</span></strong><br />\r\n<span style=\"font-family:SimSun;\">飞鸟：跟香港电影的结缘要从小时候算起了。小学时，父母赶时髦学人家唱卡拉 OK，于是家里买了台录像机，正好有个远房亲戚开了间出租录像带的小店，所以很多老港片的录像带都能从他那里拿到，那时候根本不懂得挑选，有什么看什么，看的最多的是喜剧片和武打片，比如周星驰，霸王花那些，开心鬼，五福星什么的。那时候很爱看打女，很迷胡慧中、李赛凤、大岛由加利什么的。同时，电视台每晚都会播两部香港电影，偶尔爸爸心情好，还会带我去录像厅看看武侠片。</span>\r\n</p>\r\n<br />\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1638/20130524180044_89509.jpg\" />\r\n</div>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">徐克</span>\r\n</div>\r\n<br />\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 跟香港电影结缘太久，不知不觉看了 20 多年，其实早已经不单纯是电影的东西，还有很多美好的时光，记忆，那些电影里的传奇人物，那些港片带来的感动和欢乐。对自己来说，最单纯的看电影的年代都在其中。从前看港片，年纪小就图个乐呵，港片节奏快，情节曲折，类型繁多，还喜欢玩混搭，搞得每种类型的港片都有其吸引人的地方。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 要说真正开始喜欢上某个港片导演那要到上了高中，那时候看武侠小说，所以特别迷徐克，其实现在拍的那些《武侠》、《太极》都打着“新派武侠”、“功夫电影的新突破”之类的招牌，这些东西徐老爷 70 年代末就已经玩过了。后来又迷上了吴宇森的“江湖”，迷恋那种英雄人物的豪情义气，近几年就越来越喜欢杜琪峰和许鞍华。</span><br />\r\n<br />\r\n<strong><span style=\"font-family:SimSun;\">《文周》：都是你一个人在做这个豆瓣小站么？平时生活与电影接触的多么？</span></strong><br />\r\n<span style=\"font-family:SimSun;\">飞鸟：基本上是我一个人在打理小站，所以很多时候大家吐槽说站内太冷清了，实在是分身乏术，平时工作也很忙，对小站疏于管理。也希望通过《文周》呼吁一下，希望能有更多热爱香港电影的盆友加入到小站中来，一起让这个港片迷的小天地能够活跃起来，给大家带来更多港片资讯或者资源。无论你对港片有任何方向的兴趣，都可以联系我。平时生活与电影接触应该算挺多的，我本身工作就是电影编辑，不过方向偏好莱坞，港片是纯粹的个人爱好个人情结，所以我就是，上班也电影，下班也电影。在北京影展很多，常常是奔跑在去各个影展的路上，有一次下班跟同事赶一个片儿，我们一路奔跑，忽然想到一段蒙太奇，就是两个人从年轻开始奔跑在去电影院的路上，跑着跑着便变成两个花白头发的老太，哈哈，都是真爱啊。</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1638/20130524180238_71597.jpg\" />\r\n</div>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">杨千嬅</span>\r\n</div>\r\n<br />\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-family:SimSun;\">《文周》：最喜欢或者最怀念哪个时期的香港电影？</span></strong><br />\r\n<span style=\"font-family:SimSun;\">飞鸟：最怀念的是跟我成长息息相关的 90 年代的香港电影最黄金的时期，那时候涌现的一批优秀导演和演员，大部分至今仍是香港电影的中流砥柱。你说以前的港片和现在的港片，自然大有不同，97 以后，很多香港导演都争相北上，接轨内地市场，市场大了，投资多了，自然限制也就多了，那与其去揣摩高压线，不如搞些不痛不痒的，所以就经常拍出些不知所云的东西。尤其是现在合拍片的那些个喜剧，每年贺岁档都要来骗钱的那些黄百鸣出品，王晶出品，简直不能看，笑料生硬，皆是旧桥，最重要的是，连魂都丢了，尽是些网络拼凑的破烂段子，回头看看八九十年代的香港喜剧，他们是应该羞愧的。</span><br />\r\n<br />\r\n<strong><span style=\"font-family:SimSun;\">《文周》：近年来越来越多的人都提出，香港电影开始处于一个新时代没落期，你是怎样看待的？</span></strong><br />\r\n<span style=\"font-family:SimSun;\">飞鸟：香港电影的不争气常而有之，我记得小时候也看到过很多特别粗制滥造的港片，那时只要有一种类型的电影卖座了，很多制片厂啊导演啊就争相去拍同类题材，人物脸谱化严重。有导演写剧本半天就搞定，拍一部电影七天就完工，叫做《七日鲜》，但即使在那样的状态下，一定会有导演在求思求变，香港电影让我喜欢的地方就在于此，所以我并不担心香港电影会走入一个所谓的“新时代没落期”，我们看看近年的港产片，看看《夺命金》、《桃姐》这样的优秀作品就会真心谢谢这些香港导演给我们带来的思考和感怀。</span><br />\r\n<br />\r\n<strong><span style=\"font-family:SimSun;\">《文周》：最近来说，最喜欢哪位女影星？最喜欢哪位导演？</span></strong><br />\r\n<span style=\"font-family:SimSun;\">飞鸟：女演员的话我很喜欢萧芳芳，林青霞，钟楚红。要说现在还在拍戏的年轻女演员，我比较中意杨千嬅和 fiona。</span><br />\r\n<span style=\"font-family:SimSun;\">咦？你为什么不问我男演员啊，好捉急啊，哈哈。男演员非常喜欢刘青云，我心目中最佳男主角。</span><br />\r\n<span style=\"font-family:SimSun;\">最喜欢的香港导演是杜琪峰，徐克，许鞍华。</span>\r\n</p>\r\n<br />\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1638/20130524180435_56054.jpg\" />\r\n</div>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">林青霞</span>\r\n</div>\r\n<br />\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-family:SimSun;\">《文周》：有什么想要推荐的电影么？</span></strong><br />\r\n<span style=\"font-family:SimSun;\">飞鸟：前不久刚在北京结束了由杜琪峰策划的“香港电影和电影中的香港”主题电影展。我特别高兴能看到我一直想看的电影，这次终于圆满了。强烈推荐张之亮导演的《笼民》，没有北上之前的张之亮，还是有过不少好片，像《笼民》、《自梳》、《飞越黄昏》等等。《笼民》里头的群戏棒极了，那些个老戏骨，乔宏，廖启智，刘洵一个动作一个眼神都是戏，如今的香港给人留下的印象就像个超级卖场，大家说到香港就是购物。《笼民》里面让我们看到曾经的香港有过那样的时期，底层百姓生存的艰难，小人物的命如蝼蚁，居者无其屋，在那种生活状况下，人与人之间仍然保有的那份情谊，有感动有辛酸。是一部特别好的片子。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 对了，因为北京深秋了，最近我又重温了《秋天的童话》，真是一部非常浪漫的都市情书。发哥和红姑那时候的定格再难重现，这么多年，我一直以为最后结局的 table for two 是一个“遇见就不再错过”的定格。结果，最近有朋友在看张婉婷和罗启锐的那本《雌雄大导》，对我说张婉婷并没有这个意思，而是觉得那个结尾对船头尺太残酷了，所以设置了一个再重逢，但这并不意味着两人又能够再续前缘了，遗憾终归就是遗憾。这个真实的情况实在让我太纠结了，哈哈。</span>\r\n</p>','我是好人',29,'1510.jpg',283,'','2013-05-30 13:07:09',NULL,0),(1509,'最繁华并不妨碍最美好','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">你知道，如果用女人来比喻香港的片子，这个女人肯定多的是比烂漫更繁华的璀璨，这种光芒如同最上等的钻石，拥有最完美的切工和最风情的点缀。因为夜太黑，所以光亮有时候更加明亮，甚至有些刺眼，所以我对香港</span><a href=\"http://www.manshijian.com/articles/category/dianying.html\"><span style=\"font-family:SimSun;\">电影</span></a><span style=\"font-family:SimSun;\">的态度，从来都是：可远观而不可亵玩焉。保持审美距离，不谈及爱，也不谈及恶，是最好发现‘美好’存在的方式。并且，这条定理，不局限于观影这件事情。</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1638/20130524175600_19836.jpg\" />\r\n</div>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">杜琪峰</span>\r\n</div>\r\n<br />\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 自从开始了#港味影像#这个版块，我就比往常更向往出现“时间黑洞”在我身上。我曾经以为，王家卫的片子就是最香港的，以为没有张国荣的香港电影不值一提。偏执是一种最孤勇的执著，即使不是全对，我也会过分偏执。因为只有这样，才能在我发现真相的时候，自我彻悟到，我的偏执是多么愚蠢。</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1638/20130524175639_97229.jpg\" />\r\n</div>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">王家卫</span>\r\n</div>\r\n<br />\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 除了王家卫，我开始被杜琪峰感动、被陈果震撼、被张艾嘉坚强、被张婉婷柔和，被刘镇伟调戏，被很多具名和不具名的导演激活了一颗继续奋斗在港片的心。我开始喜欢上了金城武，除了张国荣，他是第一，这个集宠爱于一身的男人，是全人类女人的理想型。看着他从《堕落天使》和《重庆森林》走来，却只是在前一段时间邂逅了他的《心动》和《薰衣草》。有时候，遇见好的电影也是需要缘分的，所以只要一旦遇到，就要好好保存下来那种瞬间和会恒久的微妙感觉，无论是用台词还是画面，都要重播几回。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 香港的东西太繁华，但这种繁华度恰巧不会妨碍它的美好。</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1638/20130524175740_16473.jpg\" />\r\n</div>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">张国荣</span>\r\n</div>\r\n<br />\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果有一天，你也被香港电影感动，或者有一天，你发现，香港电影已经成为你生命的记忆点。请记得要感谢自己，因为只有打开了心门，才会采纳每一个拥有灵魂的电影角色。（文 / 李倩菁）</span>\r\n</p>','我是好人',29,'1590.jpg',268,'','2013-05-30 13:05:42',NULL,0),(1511,'永不泯灭的摇滚精神——Beyond','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span style=\"font-family:SimSun;\"></span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130515234236_28295.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"font-family:SimSun;\">91香港红勘</span><a href=\"http://www.manshijian.com/parties/category/7.html\"><span style=\"font-family:SimSun;\">演唱会</span></a></span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;对于懂摇滚的人来说，beyond乐队是殿堂级的</span><a href=\"http://www.manshijian.com/articles/category/yinyue.html\"><span style=\"font-family:SimSun;\">音乐</span></a><span style=\"font-family:SimSun;\">乐队，他们的歌曲不仅代表了那个年代突破压抑，寻找自我的精神，那个时候，很多乐队都是靠着翻唱别人的歌曲赶场子赚钱，但是beyond乐队却凭着自己对音乐的追求，用汗水和血泪创作了一批专属于beyond的经典音乐。坊间人们会这样赞叹和描述他们创作的歌曲：“</span><span style=\"font-family:SimSun;\">有一片热土叫《大地》，有一个传说叫《长城》，有一种精神叫《不再犹豫》，有一种欣慰叫《无泪的遗憾》，有一种美丽叫《孤单一吻》，有一种感动叫《报答这一生》，有一种冲动叫《谁伴我闯荡》，有一种震憾叫《AMANI》，有一种沉重叫《祝你愉快》，有一种直白叫《喜欢你》 ，有一个脚印叫《旧日的足迹》，有一种遗憾叫《岁月无声》，有一种幸福叫《无悔这一生》，有一种沉醉叫《冷雨夜》，有一种理想叫《再见理想》，有一种呐喊叫《真的爱你》，有一种复杂叫《交织千个心》，有一种心愿叫《愿我能》，有一种落漠叫《逝去日子》，有 一股力量叫《冲开一切》，有一个年代叫《光辉岁月》。</span><span style=\"font-family:SimSun;\">”</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130515234257_74594.jpg\" /></span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span><span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;第一次听到的beyond歌曲无疑就是他们巅峰的《光辉岁月》，那时我还在读初中，自己会在学习之余听听音乐放松，那时候就会到处收集卡带放在收音机里静静的听，初中的生活其实很简单，无疑就是好好学习，考上重点高中，人人都会为了这个目标让无尽的学习压抑着自己的爱好，那时的我们因为beyond喜欢上了吉他，每次放学都会聚在一起看乐谱，练指法，时间往往轻易的流逝，我们却抓不住青春的尾巴。beyond的音乐是最发人深省的，每首歌中都带有了beyond对这个世界的希望以及对当下生活，对现实的努力。我最喜欢的一首歌并不是那些激扬高亢的，或者带有深深情怀，款款深情的曲目，而是那首beyond去非洲当场写下的</span><span style=\"font-family:SimSun;\">《AMANI》，听这首歌会让我不自觉的联想到另一位摇滚歌神MJ的《heal the worrd》。</span></span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp;现在回想那时一起弹吉他，唱beyond歌曲的青春少年们，我们已经开始学着面对社会，面对现实，还有很多已经结婚生孩子了，庆幸的是我们还不曾忘记beyond带给我们的精神，我们有空了就会一起相聚唱歌，弹吉他，还记得beyond的告别演唱会，记得家驹在舞台上激情的高歌。有时候觉得家驹在世界的某一个天地里玩音乐，有时候希望beyond复合，这是多么复杂的情绪啊，更多的时候是骂老天爷，毕竟天有不测风云，人有旦夕祸福。我们要做的就是要好好的活着，beyond的音乐经受的起时间的考验，他们不仅影响了上一代青年，同样影响着我们这一代以及后来的更多的青年追随梦想，不再犹豫。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1404/20130515234137_29073.jpg\" /></span>\r\n</p>','我是好人',26,'1511.jpg',275,'','2013-05-30 13:08:44',NULL,0),(1512,'回忆那些年我们一起追过的零食','<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">“朋友领我到食杂店，老板问她要什么，她说：‘来两瓶怀旧版的汽水。’于是老板递给我一瓶北冰洋。”最近，这样的小段子在网上颇为盛行。又快到 “六一”了，已经渐渐迈入30大关的80后们，虽然不能再过儿童节，却免不了怀旧的情结，一时间各种儿时的美味被逐一刨出来，不禁让人问一声：儿时的挚 爱，你们如今还好么？奔三的80后们，那些年我们吃过的零食还记得吗？</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135523_87834.jpg\" />\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</div>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">麦丽素</span><br />\r\n<span style=\"font-family:SimSun;\">麦丽素 怀念指数：★★★</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 要说这么多年从来没有变过的儿时味道，恐怕非麦丽素莫属了。仍旧能在各种食杂店找到的这种小吃，延续着之前的红色包装，十余年如一日，从未改变过。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 当年的麦丽素可不只是零食那么简单，由于里面加了麦乳精，就算再挑剔的妈妈，当时为了孩子的身体着想，也不会阻止孩子多吃两个麦丽素球。别的糖果，可没有这种待遇。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如今的麦丽素，还是那个味道，这样带有怀旧情怀的食品，实在适合想要怀旧的人们。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135528_39188.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">雪人冰棍</span><br />\r\n<span style=\"font-family:SimSun;\">怀念指数：★★★★</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 夏天总是要说说冰棍的。在80后的记忆里，冰棍和雪糕并不只是叫法上的不同。吃着5分钱一根的小豆冰棍的我们，是多么羡慕吃着2毛一根的雪人的同学啊。那蓬松的口感、入口的奶香，才不愧“雪糕”这个名字。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 随着经济的发展，儿时羡慕的雪糕，因为市场上品种的多元化逐渐难觅踪影。在这个怀旧的“六一”前，雪人卷土重来。记者在部分食品店看到，伊利出品了一种叫做“小雪生”的雪糕，从用料到口味都无一不与之前的雪人相似。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135533_56425.jpg\" /></strong>\r\n</div>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">大大泡泡糖</span><br />\r\n<span style=\"font-family:SimSun;\">怀念指数：★★★</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 红色的是草莓味，橙色的是橘子味，绿色的是苹果味，谁不会吹泡泡，那可是要被其他小孩子笑话的。大家都奋力锻炼吹泡技术，以达到吹出“里三层外 三层”的至高境界。现在农村小店还可以买到小块包装的，但口感貌似已不能满足我们的要求。超市也还有卖升级版的大大卷，2.9元一个。要不要赶紧试试，你 的吹泡技术退步没？</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135540_24678.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">无花果</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">怀念指数：★★★★</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 白色袋子上边印着“无花果”三个字，里面装的就是那酸酸甜甜的无花果了。这细细的干丝，既不像花，更不像果，以至于长大后看到一颗颗圆圆的无花 果，非常之困惑。现在，向阳坊、倍顺和天虹有售，3.9元一小盒。不过，最近因为全国各地买的人多，厦门已断货，还得过段时间才能到货。</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<p>\r\n		<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n	</p>\r\n	<p>\r\n		<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135546_88477.jpg\" />\r\n	</p>\r\n</div>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">跳跳糖</span><br />\r\n<span style=\"font-family:SimSun;\">怀念指数：★★★★</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 还记得第一次吃跳跳糖，惊恐不已的表情吗？一放到嘴里，它们就在口腔里噼里啪啦地跳，就像往嘴巴里放了串小鞭炮似的。1块钱一包在那时还算是奢侈品。多年过去，它的身价跟我们记忆一样，一点都没变样，还是1块钱。在学校门口的小食杂店和部分大超市都还可以买到。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135559_36428.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">大白兔奶糖</span><br />\r\n<span style=\"font-family:SimSun;\">怀念指数：★★★★</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 小时候春节时，出去拜年，最喜欢的就是吃这大白兔奶糖了。1口咬进去，浓浓的奶香，真的很满足；印象最深的一次是：有次去三舅家时，大表姐正好 买了包大白兔奶糖，结果嘞，那刚买的大白兔奶糖，当场就被我给A了--三舅家的两个表姐N好的，由于大我5、6岁，小时候每次去她们家，不管是吃的，还是 玩的，她们总会每人送我一份。</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135612_58667.jpg\" />\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">小浣熊干脆面</span><br />\r\n<span style=\"font-family:SimSun;\">怀念指数：★★★★</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我小时候是不吃方便类的食物的；不过却经常看到身边的人吃。我跟哥哥最喜欢跟三舅家的表姐表哥一起玩，而他们家有时为了方便，会直接吃这个做早 餐，呵呵~我人生的第一口小浣熊干脆面，是在大学里献出的--当时跟Ruby一起去逛超市，她看到小浣熊干脆面，很欣喜地说小时候经常吃呢，然后我说我从 没吃过，结果那孩子就定了：那咱们买包回去，宿舍4人一起吃，就当回忆小时候的那股滋味好了，呵呵。</span><br />\r\n<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<div align=\"center\" style=\"padding:0px;margin:0px;border:0px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135617_21145.jpg\" />\r\n</div>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">健力宝</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">怀念指数：★★★★★</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 小时候，整一个就是碳酸饮料控啊，每次去便利店，总会惯性地买罐碳酸饮料(尤爱健力宝)，然后一个自饮自乐，记忆里有个堂婶婶很可爱，知道我喜 欢吃橘子，喝碳酸饮料，以前每年去她家拜年时，我人刚一走到她家门口，她就会拿着橘子和健力宝来迎接我，很多时候都是在她家吃完一份，临走时她又塞给我一 份---小时候的我，怎么都不会觉得不好意思呢？</span>\r\n</p>\r\n<p style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;\">&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135636_11497.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135644_15822.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135710_64997.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135740_10579.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135805_47048.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135819_90575.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135841_35224.jpg\" /><img alt=\"\" src=\"http://www.manshijian.com/upload/member/image/1748/20130527135856_23978.jpg\" />\r\n</p>\r\n<div>\r\n	<br />\r\n</div>','我是好人',25,'1512.jpg',265,'','2013-05-30 13:10:57',NULL,0),(1514,'时光如水，总是无言','<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">岁月如河，谁也无法挽留它匆匆逝去的波涛。世事如棋，正如你说，我们相隔几千里，人海茫茫，能够相遇、相知、相亲相爱，自有一种缘把我们牵系。当我们</span><span style=\"font-family:SimSun;font-size:14px;\">有缘分</span><span style=\"font-family:SimSun;font-size:14px;\">在这个动态的时空里相逢，在历史的薄薄的书笺里，在</span><span style=\"font-family:SimSun;font-size:14px;\">时间</span><span style=\"font-family:SimSun;font-size:14px;\">湍流溅起的一束浪花中。面对熟悉的往事渐淡渐远，陌生的未来尚难辨析，你我在浩淼的世界里就如几粒细细的砂子，偶然地紧挨在一起，共同承沐这金质的阳光，这清新的空气，这纯蓝的天空，这墨绿的丛林，这开满野花的草地，安排了我们不可更改的相约，也许这就是我们的缘……</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　遇见你的那一刻是我心里最灿烂的悸动，脸上竟然露出久违的笑容，天地万物都成了虚无。我竟然不敢相信自己还能有那份热情和渴望。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　爱上你是我心中最美的期待，庆幸我等到了自己想要的爱情，从荒芜到芳菲、从平静到热烈、从冷漠到释放，这是我从未想到过的改变。有你陪伴的日子是我生命中的天堂，有你就仿佛拥有整个世界；可以不吃、不睡，想着你的一切，被甜蜜装得满满的，你就是我心中的幸福满屋。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　今生，你若为莲，我便惜莲；你若为花，我便护花；你若为蝶，我便随君舞天涯。你若不离，我便不弃！</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　相信人世间有一种相遇不是在路上，而是在心上；有一种守候不在于朝朝暮暮，而是心心相携。生命中有一份真情只要付出，便是幸福，一声懂得便是花开。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　今生与你千里姻缘，携手相恋、唇齿相依、十指相扣的缠绵。愿得一人心，白首不相离的痴觉。此去经年伴你天涯不言悔，只为回眸处你能看到我的浅笑嫣然。时光静好，与君语；细水长流，与君同；繁华落尽；与君老。亲爱的，真的好想牵着你的手，感受你掌心的温度；真的好想与你携手看花、并肩赏月；一起看朝霞起、夕阳落。执一缕淡淡的清欢，与你一起慢慢变老，做你手中的宝。每天清晨阳光与你同在，就是我此生最大的幸福！</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　我们分开的日子，仿佛是我狱练的开始，一切回到原点，只是多了一份空虚、一份寂寞、一份刻骨铭心的思念……</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　红尘深处，我与你浅唱，为你红袖添香。剪一抹相思的暖、掬一抹思念的香，与你在寂寞流年里相依相暖。初遇时，镌刻美好，回眸处，相思已入骨。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　谁的真情绵长了谁的相见恨晚？谁的守望润湿了谁的眼？等待终是山高水长。想你时你在天涯，念你时你在海角。老天捉弄人，总是让我们南北交替，想见却不能相见，真是好事多磨。你不在的日子，思念在我心中嫣然花开。我将牵念托与清风明月。将等待寄与日月流年。想你，久念成痴。你的一声问候，抵得上人间万千的暖。你的绕指柔情，是我此生最大的眷恋……</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　今生，许你一段真情，不为天长地久，不为朝夕相守。只为你若快乐，我便幸福。今世，续你一段前缘，不求开花结果，不问缘深缘浅，只为你若安好，我便是晴天。回眸间，至少今生我们没有错过，终是无悔这一场倾心的相遇。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　素年如锦，你在远方寄来牵念，与我吟唱指尖最美的年华。任花开花落，任幸福在心中开出一节节的繁花。你为我点亮了心灯，给予了我一米阳光，光彩夺目，明媚了我的眼眸。我为你舞尽落红，天涯海角永相随。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　为你，纵然爱情是一杯苦酒，我也会含笑饮下不言悔。为你，纵然相隔千山万水，我也望穿秋水不思归。无论等待让我多么的焦虑，我依然会静静的等你，只因爱你，无关其它，爱你，只是爱你。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　当指尖划过流年的时候，你成为我生命里的暖。你的真情温暖了我，和你在一起的时刻是我今生最美的记忆，你的眷恋是我一生的守候。我知道无论我走到哪里，你都会在我心里。任年华如玉，终有一瓣心香伴我度过人间烟火，终有一抹柔情温暖我的微凉。此生，愿与你相看流年不言悔，愿与你执子携手、与子皆老！</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"font-family:SimSun;font-size:14px;\">　　时光如水，总是无言。你的似水柔情在我心中开成一朵繁花。暗香四溢。前生，你在我的眼眸里，今生，你在我的牵念里。若有来生，你在我的轮回里。素色流年，携一抹温暖与你心心相携，捻一抹牵念伴你浅舞天涯。花开相惜，花落不弃，红尘摆渡，有你才幸福，有你才能</span><span style=\"font-family:SimSun;font-size:14px;\">完美</span><span style=\"font-family:SimSun;font-size:14px;\">！</span> \r\n</p>','我是好人',28,'1514.jpg',252,'','2013-06-06 14:36:19',NULL,0),(1515,'你最想知道的冬季三亚旅游攻略','<p style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	岁末年初，三亚温暖的阳光大海令人着迷和想往。亚龙湾的海滩、豪华酒店、鸟巢树屋；天涯海角、大小洞天、南山的优美风光；呀诺达、槟榔谷的特色风情；海鲜大排档里的海鲜盛宴；免税店里的疯狂购物……\r\n</p>\r\n<p style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	去三亚，其实还有很多大家普遍关心的问题，如价格、线路、住宿等热门问题。小编通过整理艺龙旅游专家和在地三亚人的攻略和建议，整理了三亚冬季旅游问答，希望能对冬天、春节去三亚的朋友有所帮助。\r\n</p>\r\n<p style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	&nbsp;\r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	<img alt=\"\" border=\"0\" src=\"http://img.trip.elong.com/guide/attachments/8f/aa/72/8faa72102ac14d1c9b0e792c419459f3.jpg\" style=\"border:1px solid #E4E4E4;\" /> \r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	冬天到三亚去度假（图片来源：旅游专家 冰城馨子）\r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	&nbsp;\r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	<img alt=\"\" border=\"0\" src=\"http://img.trip.elong.com/guide/attachments/6b/28/40/6b28405d6edf1ab6c650c11eb82bf49c.jpg\" style=\"border:1px solid #E4E4E4;\" /> \r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	鸟巢度假村鸟瞰亚龙湾（图片来源：旅游专家 锈剑）\r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	&nbsp;\r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	<img alt=\"\" border=\"0\" src=\"http://img.trip.elong.com/guide/attachments/11/52/68/115268cb1f5817da8fc19556bdd08009.jpg\" style=\"border:1px solid #E4E4E4;\" /> \r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	三亚的落日余晖（图片来源：阿滋猫）\r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	&nbsp;\r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	<img alt=\"\" border=\"0\" src=\"http://img.trip.elong.com/guide/attachments/c1/03/ca/c103cad50f574436a9cfdff0493d15c9.jpg\" style=\"border:1px solid #E4E4E4;\" /> \r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	鸟巢树屋，浪漫首选\r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	&nbsp;\r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	<img alt=\"\" border=\"0\" src=\"http://img.trip.elong.com/guide/attachments/59/71/27/597127208dfa164023816270950d3852.jpg\" style=\"border:1px solid #E4E4E4;\" /> \r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	蜈支洲岛潜水，和鱼儿共舞（图片来源：旅游专家 阿滋猫）\r\n</p>\r\n<p align=\"center\" style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;background-color:#F6FAFF;\">\r\n	<b>提问：</b>2014年春节期间三亚酒店价格会很高吗？\r\n</p>\r\n<br />\r\n<b>回答：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">春节期间是三亚的旺季，酒店价格会比平日提高不少。</span><br />\r\n<br />\r\n<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">2014年春节期间，三亚旅游饭店客房价格实行政府指导价管理。采取最高限价和超限报批相结合的方式。2014年春节期间旅游饭店标准客房备案价格不得高于2013年春节期间标准客房备案价格。原则上标准客房最高限价为每间每天5000元。&nbsp;</span><br />\r\n<br />\r\n<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">此外，对高端旅游饭店自助餐价格实行临时价格干预措施。单独计价的旅游饭店自助餐，早餐价格不超过每位350元，中晚餐价格不超过每位500元。这些价格均包括各类手续费、服务费。&nbsp;</span><br />\r\n<br />\r\n<b>提问：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">三亚什么时间去最好？价格合理，气候宜人？</span><br />\r\n<br />\r\n<b>回答：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">每年的十月至四月是三亚最美丽的时候，特别是冬天，当北国千里冰封万里雪飘时，三亚依然气候在二十五度左右，且夕阳西下风光绚丽。元旦之后至春节之前价格还合理。春节期间人多且价格高。</span><br />\r\n<br />\r\n<b>提问：</b>三亚湾<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">、</span>大东海<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">、亚龙湾各有什么特点？好纠结住哪儿。</span><br />\r\n<br />\r\n<b>回答：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">三亚三个海湾的酒店代表中国目前最高档的海岸酒店。</span><br />\r\n<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">亚龙湾集中了五星豪华酒店，其特点可欣赏亚州第一湾亚龙湾海天一色风光；</span><br />\r\n<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">大东海是三亚最早开发的海湾，以三四星为主是目前三亚人气最旺购物的天堂所在；</span><br />\r\n<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">三亚湾因其十公里漫长的海湾，除了四五星的海景酒店外，公寓和快捷酒店也很多，因紧邻传统三亚胜利路老街道夜晚出行方便，更是因为夕阳最美而人气最旺的海湾之一。</span><br />\r\n<br />\r\n<b>提问：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">想下海游泳，哪里的沙滩好？</span><br />\r\n<br />\r\n<b>回答：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">亚龙湾的沙滩应该是三亚最美的，一般</span>临海<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">的5星酒店都有自家的私人海滩，酒店客人是免费试用的，人也会比较少。不过冬天虽然阳光明媚，但是海水还是比较凉的，如果要下海，还是选择在中午时间。</span><br />\r\n<br />\r\n<b>提问：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">三亚景点之间距离不是很近，如何前往比较划算？</span><br />\r\n<br />\r\n<b>回答：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">相对而言，三亚传统景区分东西二线。比如天涯海角、南山、大小洞天等都在西线约离市区二十公里左右，平时打车也就是三十元左右。有公交直达。而东线的亚龙湾、鸟巢等却有六十公里左右。虽有车去但比较少。</span><br />\r\n<br />\r\n<b>提问：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">三亚特色美食和吃美食的小店有哪些？</span><br />\r\n<br />\r\n<b>回答：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">海鲜是三亚的最基本特色美食，</span>南海<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">的海鲜以虾蟹鱼为主。将海鲜打边炉是三亚最基本的吃法。最有名的大排档是三亚春园与第一市场的海鲜大排档。湘菜、</span>四川<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">菜比较便宜的小店都在商品街道一带，商品街就在三亚河东岸市区内。</span><br />\r\n<br />\r\n<b>提问：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">3-5天时间，怎么玩三亚？</span><br />\r\n<br />\r\n<b>回答：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">第一天西线寻古拜佛问道福天洞地，主要景区：沿天涯海角 南山、</span>小洞天<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">，直达古崖州府所在地崖城；第二天，东线看海上山觅雨林览鸟巢赏黎苗风情。主要景区，亚龙湾、鸟巢、呀诺达、槟榔谷。第三天，市区品海鲜吃水果逛超市三亚湾畔看落日。白天在市区内的大东海、三亚湾看海景，夜晚上鹿回头看市景。</span><br />\r\n<br />\r\n<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">其它时间可以用来在海边悠闲的度过，或者安排一次三亚免税店的购物旅程。</span><br />\r\n<br />\r\n<b>提问：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">除了看海，三亚最好玩的地方在哪里？</span><br />\r\n<br />\r\n<b>回答：</b><span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">一，大小洞天，集八百年历史中国海边的道教洞天景区，它是三亚最悠久景区南宋开基，也是欣赏三亚看海上落日的最佳地！</span><br />\r\n<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">二。天涯海角景区是三亚最基本的标志，站在那块石垒留下影则是到了三亚一种象征！</span><br />\r\n<span style=\"color:#434343;font-family:宋体, \'Arial Narrow\', HELVETICA;font-size:14px;line-height:23px;background-color:#F6FAFF;\">三，据离三亚六十公里的呀诺达景区，则是代表大三亚热带雨林最著名山景。槟榔谷则集中了三亚所在的琼南地区黎苗风情。</span>','我是好人',27,'1515.jpg',0,'','2013-12-04 10:53:59',NULL,0),(1516,'蓝色情怀（诱人的西栅夜色）','<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"color:black;\">我个人偏爱西部风光，这些年足迹走遍西部五省，去年因工作原因有机会到江南走了一圈，见到的是另外一种风格；那些人文的粉墙黛瓦、小桥流水与悠久的历史底蕴无疑让我对江南有了新的感受和认识。特别是江南的水乡，那种夹杂着渊源文化的村镇透露出细腻色彩。这里我带你去</span><span style=\"font-family:arial;color:black;\"><span style=\"font-family:宋体;color:black;\">乌镇</span></span><span style=\"color:black;\">看看吧，当然首先要看看乌镇的夜。那是</span><span style=\"font-family:arial;color:black;\">2012</span><span style=\"color:black;\">年</span><span style=\"font-family:arial;color:black;\">2</span><span style=\"color:black;\">月</span><span style=\"font-family:arial;color:black;\">14</span><span style=\"color:black;\">日的一个阴雨连绵的日子，我们从</span><span style=\"font-family:arial;color:black;\"><span style=\"font-family:宋体;color:black;\">杭州</span></span><span style=\"color:black;\">出发经过</span><span style=\"font-family:arial;color:black;\">80</span><span style=\"color:black;\">多公里的驾驶，上午十点半到了乌镇，入住位于</span><span style=\"font-family:arial;color:black;\"><span style=\"font-family:宋体;color:black;\">西栅</span></span><span style=\"color:black;\">景区门口的锦江之星酒店，不过房间费用比较高大床商务间要</span><span style=\"font-family:arial;color:black;\">240</span><span style=\"color:black;\">元，但房间蛮大的。其实按我的性格喜欢住在景区内的客栈，但是由于此次为商业活动，只好选择酒店啦。吃过午饭我们便开始游览景点，乌镇分为东、西栅景区，单票东</span><span style=\"font-family:arial;color:black;\">100</span><span style=\"color:black;\">元西</span><span style=\"font-family:arial;color:black;\">120</span><span style=\"color:black;\">元联票为</span><span style=\"font-family:arial;color:black;\">150</span><span style=\"color:black;\">元，首先我们来到东栅景区。景区游程达</span><span style=\"font-family:arial;color:black;\">2</span><span style=\"color:black;\">公里，由东栅</span><span style=\"font-family:arial;color:black;\"><span style=\"font-family:宋体;color:black;\">老街</span></span><span style=\"color:black;\">、观前街、河边水阁、廊棚组成，工程全部完工后，东栅景区占地面积约为</span><span style=\"font-family:arial;color:black;\">0.9</span><span style=\"color:black;\">平方公里，设有十多个经典，如果细细品味这里可供你游玩半天。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-family:arial;color:black;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"color:black;\">在这里我想特别介绍一下西栅的夜，好容易熬到天黑，当夜幕降临时，这里的景观灯依次亮起来，无论是河提上、巷子里、桥头亭榭都披上一层绚丽地光彩，五颜六色的灯火交相辉映，雨还在下，在光束里它们像根根发丝从天飘落，游客逐渐躲起来，他们都被眼前的璀璨迷住。如果你带着情人或者亲友在岸边找个地方喝起小酒，品着香茶，看对岸楼台上唱戏，或者到水边放几盏莲花灯，都会令人心醉。。。。。。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-family:宋体;color:black;\">天色渐渐暗下来，黄昏下的小桥流水更加魅力迷人，夜晚的西栅在景观灯的映射下现出无穷的色彩和魅力。我没有带脚架，为了最大限度地拍好这些美景，我趴在湿漉漉的桥栏上、倚在墙角边，屏住呼吸轻轻按下快门。古老的街巷在夜色里更显悠久的韵味，经过雨水冲刷过的街区更增添了怀旧的情调。小雨轻轻抚摸着肌肤，清凉湿润的空气里充满自然清醇的气息，蓝色的街灯诉说着遥远的传说。。。。。。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560557f8cH.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560559kmQS.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560562eTTS.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560564xGP0.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560566hqW5.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560567Ot7k.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_13855605706JmR.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560572AeBS.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560573ykbP.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560575Mgcw.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560578TWd2.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560580lUqO.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_13855605828mqK.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560584lavG.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560586cykY.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560592ypCw.jpg.big.jpg\" width=\"600\" height=\"422\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560598qjdj.jpg.big.jpg\" width=\"600\" height=\"422\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-family:宋体;color:#333333;\">&nbsp;&nbsp;&nbsp;&nbsp;那么如果你恰好赶上一个烟雨天气，无疑平添了一股朦胧的诗意，灰蒙蒙的雾气笼罩着交错的小桥，模糊的天际构成一幅流水人家最温暖的画面。下面是一组乌镇白天的图片，希望您喜欢。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-family:宋体;color:#333333;\"></span>&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560847fVTJ.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560849RuC1.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_13855608502N9D.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560852vXE6.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560854E7AH.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560856EZnx.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560859QaAO.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560862wPrB.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_13855608641O21.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560865Jatp.jpg.big.jpg\" width=\"600\" height=\"360\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560868SiQ0.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_1385560870OFbJ.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/27/5408466_13855608724rey.jpg.big.jpg\" width=\"600\" height=\"400\" />\r\n</p>\r\n<div>\r\n	<br />\r\n</div>','我是好人',27,'1516.jpg',2,'','2013-12-04 10:56:06',NULL,0),(1517,'拜望乐山大佛','<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\">&nbsp;乐山大佛，地处中国四川省乐山市，岷江、青衣江和大渡河三江汇流处。大佛立于凌云山，高71米，开凿于唐代开元元年（713年），完成于贞元十九年（803年）。千百年来，大佛以其超然淡定的神态屹立于风雨沧桑之中，也带给过往行人更多感悟。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622792wbv4.jpg.big.jpg\" width=\"600\" height=\"399\" /></span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\">1、通往大佛景区的宽敞道路口，立有一造型独特精致的大牌坊。过牌坊及进入景区，沿鲜花大道步行不远就到了大佛入口验票处。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"></span>&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622645YiiS.jpg.big.jpg\" width=\"600\" height=\"399\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\">2、沿步游道而上，路旁石窟内供奉着各路仙像，遗憾的是很多毁于文革时期，有窟无像了。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622657xGXI.jpg.big.jpg\" width=\"600\" height=\"399\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\">3、步游道旁，随处可见的崖刻无不显现出乐山大佛厚重的文化底蕴。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"></span><br />\r\n<img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622744vp7B.jpg.big.jpg\" width=\"600\" height=\"399\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\">4、错落有致的梯道，斑驳中显露风骨。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"></span>&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622730dbMG.jpg.big.jpg\" width=\"532\" height=\"800\" /></span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\">5、北有香雪海，此有香雪溪。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"></span>&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622718yqny.jpg.big.jpg\" width=\"600\" height=\"399\" /></span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\">6、碑刻</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"></span>&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622770hTrz.jpg.big.jpg\" width=\"600\" height=\"399\" /></span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\">7、东坡院。幽雅静谧、苍翠花香之地为大文人最爱。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"></span>&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622756wRux.jpg.big.jpg\" width=\"532\" height=\"800\" /></span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\">8、灵宝塔。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"></span>&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622668NoWP.jpg.big.jpg\" width=\"532\" height=\"800\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	9、藏经楼\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622679qX2M.jpg.big.jpg\" width=\"600\" height=\"399\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\">10、凌云寺内。缭绕的香雾带着香客的各种愿望缓缓升腾飘散。</span>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<span style=\"font-size:18px;\"></span><br />\r\n<img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622782BxaG.jpg.big.jpg\" width=\"600\" height=\"399\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	11、福寿天地。\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622693xpuA.jpg.big.jpg\" width=\"532\" height=\"800\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img src=\"http://img.trip.elong.com/home/attachment/201311/28/5237011_1385622705yzfz.jpg.big.jpg\" width=\"532\" height=\"800\" />\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<br />\r\n<span style=\"font-size:18px;\">12、超然淡定、巍然端坐的大佛，见证了千年的风雨沧桑、世事变幻，依然承载着善良百姓托付的美好梦愿立于大渡河、青衣江、岷江的三江交汇处。</span>\r\n</p>','我是好人',27,'1517.jpg',0,'','2013-12-04 10:57:53',NULL,0),(1520,'方文山“牵手节”档 获周杰伦力挺','<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;text-indent:2em;\">\r\n	<strong>“牵手节”档独领风骚 终极版海报首曝光</strong> \r\n</p>\r\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;text-indent:2em;\">\r\n	牵手节的起源主要是因为光棍节，是11月11号光棍节之后被广为认可后的又一个节日。起因是每到光棍节人们都是互相说笑如何“脱光”，因此之后就有了12月12日牵手节，它代表着由单变双，成双成对，且“2”字像人的手一样弯曲以便手牵对方。\r\n</p>\r\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;text-indent:2em;\">\r\n	而要说到“牵手节电影档期”，这在国内电影市场还是破天荒头一遭。电影《听见下雨的声音》正是一部清新唯美的爱情电影，讲述的是一位乐团主唱与失聪女大学生的纯爱故事。片方独具匠心地将影片档期选在了12月12日牵手节，也是希望在这个日渐寒冷的初冬季节，带给大家一道催人泪下的爱情大餐。在这个充满浓情的时刻，让12月12日真正成为你的牵手日，有爱就请大声说出来！\r\n</p>\r\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;text-indent:2em;\">\r\n	而在观众被12月上旬<span class=\"infoDataBank\">《无人区》</span>、《四大名捕2》、《风暴》等各类大片轮番轰炸眼球的时候，《听见下雨的声音》恰恰是同档期唯一一部爱情题材影片。有专家分析，这部清新感人、直指内心的爱情题材影片，极有可能在“牵手节档”异军突起，成为约会男女的唯一选择。\r\n</p>\r\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;text-indent:2em;\">\r\n	除了打造全新的“牵手节”档期，片方同时还公布了一款终极海报。海报中，四大主演徐若瑄、柯有伦以及释小龙、韩雨洁之间的关系扑朔迷离；而“墙角的青苔入镜，跳接檐下的风铃，于是我们听见，下雨的声音”这样的唯美诗句赫然入眼，不经意间透露出有着“亚洲第一词人”之称的导演方文山的创作鬼才。\r\n</p>\r\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;text-indent:2em;\">\r\n	<strong>周杰伦跨刀打造主题曲 全明星阵容力捧方文山</strong> \r\n</p>\r\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;text-indent:2em;\">\r\n	从《青花瓷》《千里之外》到《菊花台》、《黄金甲》……，方文山和周杰伦联手创造了华语乐坛一个又一个经典，如今两人又各自跨行当起了电影导演，这回得知方文山要打造自己第一部电影时，与方文山有着十几年交情、曾同甘苦共患难的好哥们周杰伦，义不容辞全力支持。电影《听见下雨的声音》同名主题曲，便是由周杰伦精心作曲的；而在方文山为期两年的拍摄中，周杰伦曾专门到台湾和新加坡的片场探班，方文山表示：“杰伦从影经验比我丰富，在我们的互相交流中，我也有很多的收获。”\r\n</p>\r\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;text-indent:2em;\">\r\n	除此之外，方文山的处女作也得到众多明星大腕的鼎力支持，可谓全明星超豪华阵容。电影《听见下雨的声音》里不仅有徐若瑄、释小龙、徐娇、柯有伦等实力明星担纲主演，更有亚洲人气天团SJM成员周觅、“台湾第一美女主播”侯佩岑、“Live天王”林志炫、 “果郡王”李东学以及歌手品冠等众多大腕倾情加盟客串，可以说，这些都是方文山长期以来积攒下来的超好人缘的结果。\r\n</p>\r\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;text-indent:2em;\">\r\n	电影《听见下雨的声音》在台湾放映时取得了不错的反响，如今自己的处女作即将接受内地观众的检验，导演方文山也有自己的期许：“我希望大家来看，不会觉得浪费时间或钱，因为我拍得很认真，有使命感在里面。我可以很肯定地说，这部电影很方文山的感觉。\r\n</p>','我是好人',29,'1520.jpg',1,'','2013-12-04 11:11:34',NULL,0),(1519,'秋日南岭赏红叶攻略','<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<strong>推荐理由</strong>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	　　广东的城市人对夏天的感受最为明显，对秋天的季节最为期盼，但毕竟秋天太短。天高气爽的秋日，越来越可贵，当下正是出游最佳时节，在广东境内搜寻秋游至美之地，莫过于韶关南岭国家森林公园。广东秋意渐浓时，南岭的秋叶也开始被染红，从小黄山顶峰，渐次红至亲水谷的低洼地区，秋日漫山遍野，红叶斑斓，美景如画。只需3小时的车程，就可以远离都市尘嚣，投入南岭国家森林公园的怀抱，呼吸到纯净空气青草花香，遍看秋日红叶烂漫。\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	　　<strong>-行程亮点</strong>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<strong>　　落红点点，情趣万般</strong>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	　　从10月下旬开始，红色旋风将蔓延整个南岭。南岭的红叶多为三角枫、槭树，叶如纤纤玉指尽情舒展，色彩纯净，如南国佳丽。远看，那成片的红叶映衬着成片的绿叶，漫山遍野因红叶、黄叶的点缀，更像是浑然天成的色彩斑斓的水彩画。近看，那片片红叶晶莹透明，风姿绰约，特别是那红叶落在地上，落在水上，落红点点，情趣万般。霜重露浓、天高云淡的深秋，在远离都市喧嚣的南岭国家森林公园，在真山真水中健行，寻找广东最美的那一片红叶，寻找到内心那久违的安宁，体会戒言慎行的真意，带着这份心境游历南岭实为舒畅。秋天的南岭除了红叶，还有“蓝松”。尤其是此时的小黄山景区，满山的广东松换上了光彩夺目的粉蓝色，让这片原始森林更富层次感，因此，岭南画家喜以此地的“蓝松”入画。\r\n</p>\r\n<p class=\"moduleSingleImg01\" align=\"center\" style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<img alt=\"南岭下的村庄\" src=\"http://i3.sinaimg.cn/travel/ul/2009/1012/U3405P704DT20091012162658.jpg\" border=\"1\" style=\"border:none black;\" />\r\n</p>\r\n<div class=\"moduleSingleImg01\" align=\"center\" style=\"margin:0px;padding:0px;color:#434343;font-family:Simsun;background-color:#FBFCFF;\">\r\n	南岭下的村庄\r\n</div>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	　　<strong>不登南岭，枉为广东人</strong>\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	　　沿途欣赏红叶蓝松，呼吸清新氧气，直到爬上最高峰———石坑崆。作为广东人，不登南岭山峰，实为憾事。南岭群峰起伏，千米以上的山峰有30多座，其中以有“广东第一峰”之称的石坑崆最为出名，海拔高达1902米。山顶由多个小石峰组成，向东连绵起伏，极为峻峭，是华南最雄伟的花岗石山峰之一。\r\n</p>\r\n<p style=\"color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	　　盛产“果王”猕猴桃、满身长刺的山毛榉、鲜红酸甜的树莓，还有美味的野荔枝。野荔枝果实外形像小荔枝，果肉洁白，清甜馥香。常有游客在路边拾到大串野荔枝，惊喜地剥开想试试，却发现每个果实上都有个小洞。原来馋嘴的短尾猴早已抢先吃掉了。山中还有一种当地人称为“猴欢喜”的野果，果子熟透之后呈鲜红色，远看像杨梅，经常引得猴子前来摘食。然而果子却并不好吃，害得猴子空欢喜一场。\r\n</p>\r\n<div class=\"blkContainerSblkCon\" id=\"artibody\" style=\"margin:0px;padding:0px;color:#434343;font-family:Simsun;font-size:14px;background-color:#FBFCFF;\">\r\n	<p class=\"moduleSingleImg01\" align=\"center\">\r\n		<a name=\"page_pic\"></a><br />\r\n<img alt=\"南岭橙屋\" src=\"http://i1.sinaimg.cn/travel/ul/2009/1012/U3405P704DT20091012162742.jpg\" border=\"1\" style=\"border:none black;\" />\r\n	</p>\r\n	<div class=\"moduleSingleImg01\" align=\"center\" style=\"margin:0px;padding:0px;font-size:12px;\">\r\n		南岭橙屋\r\n	</div>\r\n	<p>\r\n		　　<strong>-贴士</strong>\r\n	</p>\r\n	<p>\r\n		<strong>　　交通：</strong>\r\n	</p>\r\n	<p>\r\n		　　自驾车出游线路：上华南快速干线———京珠高速公路(过曲江，韶关)———乳源县大桥站出口下高速，全程约为285公里，在“大桥”高速公路收费站交费后，前行50米右转。再往前1.8公里左右到丁字路口，转右(上可看到指示牌：南岭国家森林公园或五指山)，直行8公里到五指山生活区即可见橙屋酒店，行程约需3.5小时。\r\n	</p>\r\n	<p>\r\n		　　粤北山区气温较低且天气多变，应多带一件衣服和雨具。森林里比较湿滑，最好穿上防滑的鞋，要穿长裤和长袖以防止虫咬。\r\n	</p>\r\n	<p>\r\n		　　当地最高规格的酒店是橙屋酒店。南岭山脚下有不少小旅馆，但接待条件与橙屋有差距。\r\n	</p>\r\n	<p>\r\n		　　每年秋天是吃山坑螺的最佳时节，韶关地区爆炒山坑螺的方法很精细，到韶关一定要吃爆炒山坑螺。龙归冷水肚是曲江龙归镇的特色菜，这道菜的具体烹制方法是保密的，所以只有去龙归才可吃到最正宗的。另外，还有特色石头猪肉、招牌花生豆腐、野山菌蒸走地鸡。\r\n	</p>\r\n</div>','我是好人',27,'1519.jpg',0,'','2013-12-04 11:06:03',NULL,0),(1521,'黄粱一梦二十年','<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">陈升是个又老又胖又不好看的男人，据传说在练铁人三项。“这世界有点假，而我莫名爱上TA。”这句歌词是这个男人对我最初的打动。2008年陈升50岁的时候发了专辑，《美丽的邂逅》。于是我们邂逅了这首《牡丹亭外》。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　黄梅调邂逅木吉他，女驸马邂逅牡丹亭。看似不太搭调的一切，被陈升不紧不慢的揉在一起。听着听着，你会被他莫名打动。多少往事就这样穿云破雾，汹涌而至，又像时光一般散去。写这首歌的时候，我猜，他也一定喝了酒吧!　一开始听到这个名字以为和Jay的《千里之外》一样的，很不以为然的点击试听。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　一个貌似五音不全的男人自顾自的不知道在叽歪什么。后来我听懂了歌词，觉得陈升简直把情字写绝了，也唱绝了。 再后来，我也开始把这首歌推荐给别人听，收获的当然大多数都是哂笑说你怎么听这种歌?这时候我一般都是笑笑，说是啊是啊，还蛮不一样的哈。我都不敢说我很喜欢啊，多好的歌，多动人。毕竟，怕别人的眼光。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　许巍汪峰还在唱着青春幻象的时候，陈升已经一眼望穿命运的终局。在陈升这里，没有“你是茫茫人海之中我的女人”，只有“牡丹亭外雨纷纷，谁是归人说不准，是归人啊你说分明，你把我心放哪儿”。可是，唱歌的他和听歌的我们，其实都还令人难以置信的乐观。唱歌的时候一定要仿佛自己什么也不在乎一般，生活的时候却还需要该吃饭就吃饭，该流泪就流泪，该谈爱情的时候谈爱情，该给孩子买</span>早餐<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">就卖早餐，该跟妻子之外自己其实心疼的人分手的时候，就分手。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　他独特的嗓子唱“写歌的人假正经，听歌的人最无情。”十四个字道尽苍凉，在这个世上，每个人的人生几乎都是一场纷扬大戏，只不过有的是喜剧，有的是悲剧，有的是正剧，更有些人悲喜交集，不到落幕，谁也不会知道自己的人生，究竟会是怎么样一个结局。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　时间仿佛一条长河，它会改变很多，可是新的改变，未必不是好的。只能感叹年华易逝。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　“写歌的人假正经，听歌的人最无情。”</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　听歌的人最无情，因为把耳机一摘，其实一直都知道该干什么，该怎么干。一点悬念都没有，其实也不需要悬念。假正经和真无情，似乎已经成为了当下大多数人的一种状态，</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　王小波说，人除了现实中庸俗的物质世界外，其实都拥有属于自己的诗意世界。我们在一天天的长大，接受着生活不断的砥砺;我们也在一天天的远离</span>童年<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">和少时自己编织的美丽而多彩的诗意的世界。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　每每想到这些，我就会莫名的焦虑和悲伤。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　信息渐发达，美丽却渐远了。我们在两个世界中徘徊，每个人都是自己的船长，却总不能唤醒自己，遂了心愿。于是，停靠在现实中的时间越来越长，诗意的世界渐行渐远。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　事业、爱情、生活、亲情、友谊等等扯不断理还乱的奇怪关系让我忘了自己的生活还有许多需要改进，忘了自己还怀揣着最初的梦想。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　陈升的</span>音乐<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">独树一帜，陈升的音乐为不能醉而悔，陈升的音乐为不能醒而痛，他的音乐有人格的真境，有酒精的血性，有浮世尘埃的幻化，有摇滚性情的率真，有震颤心弦的风魔……</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　我是从《把悲伤留给自己》开始一步步地进入陈升的音乐世界。有才之人就像一座宝藏，无论怎么挖都有惊喜，而且向来是源源不绝，比如陈升。由此开始，我尝试着从每个阶段的体会中去感受和理解陈升歌中的每一种状态，久而久之，生活中的每一种状态都能够从陈升的音乐中感受到。</span>','我是好人',26,'1521.jpg',0,'','2013-12-04 11:18:54',NULL,0),(1522,'音乐是记忆的载体','<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">我总觉得，有很多时候，记忆不是单纯储藏在脑袋里，而是寄居在某些特定的东西上面，因此才可以持久而坚定地依附在我们的身体上，不随时间的流逝而消失，或许会在一段时间变淡，但是只要这种被依附的介质出现，记忆也就出现了。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我的大部分记忆总是依附在</span><a href=\"http://www.manshijian.com/articles/category/yinyue.html\">音乐</a><span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">上。一个</span><a href=\"http://www.manshijian.com/articles/category/dianying.html\">电影</a><span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">或者一首</span><a href=\"http://www.manshijian.com/articles/category/yinyue.html\">歌曲</a><span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">，都代表着一段时间的记忆，很早前自己有个习惯，就是写文章的时候喜欢听一首歌，也没有特定的要求，只要曲风和调子满足当时的心境就可以了。而后，在某次重新听到这首歌的时候，自然当时的情绪一下子就上来了。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 记得有一年冬天的圣诞节，窗外下着很大的雪，整个房间里只有我一个人，安静而空灵，我在伏在桌子上突然觉得这个世界凸显出一种平时没有的样子：窗子外边不远处是喧闹的烟火和人群，繁华而绚烂，隔着窗子我能感受到热闹的气氛，却听不到任何声音，只能看到这一片繁华。我打开电台，正好在放陈奕迅的圣诞结，很应景。说不上什么感觉，只觉得有些相同的感觉。有时候世界太喧闹的时候，却不想被人群淹没，反而享受这一份别样的孤寂。别人可能会觉得这是孤独，是不好的感受，但是我却觉得并不是如此，热闹近在咫尺，我大可走出房子去一起欢乐，但是我却极其享受这一份难得的自我空间。有谁说过，这个世界总有一半人不理解另一半人的快乐，向来如此。大概就是这个意思吧。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;后来的很多个圣诞节，我再也没有这样的感受了，可能是心境不一样了，即使刻意避开人群和邀请，一个人呆在屋子里，感受到的往往差那么一点火候。有些感觉，真真是可遇而不可求的。还好有这么当时一首歌，记录下我愉快而珍贵的独处情绪。有时候，即使是走在闹市中，突然有这首歌飘到我的耳朵里，我都会觉得整个记忆的神经被激活了，顿时嘴角不自觉上扬，只有我懂的快乐。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我想说，有时候记忆就是这么神奇的存在，我有很多的记忆都是这样依附在音乐上，开心，伤心，失望，惊喜，很多很多的情绪，像小蜜蜂住在蜂巢里一样，住在不同的歌曲里。甚至，有时候不仅仅是歌曲，还是电影，是味道，是颜色，也是触感。</span>','我是好人',26,'1522.jpg',0,'','2013-12-04 11:19:44',NULL,0),(1523,'很想去看贡寮的海洋音乐祭','<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">我总觉得，有很多时候，记忆不是单纯储藏在脑袋里，而是寄居在某些特定的东西上面，因此才可以持久而坚定地依附在我们的身体上，不随时间的流逝而消失，或许会在一段时间变淡，但是只要这种被依附的介质出现，记忆也就出现了。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我的大部分记忆总是依附在</span>音乐<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">上。一个</span>电影<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">或者一首</span>歌曲<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">，都代表着一段时间的记忆，很早前自己有个习惯，就是写文章的时候喜欢听一首歌，也没有特定的要求，只要曲风和调子满足当时的心境就可以了。而后，在某次重新听到这首歌的时候，自然当时的情绪一下子就上来了。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 记得有一年冬天的圣诞节，窗外下着很大的雪，整个房间里只有我一个人，安静而空灵，我在伏在桌子上突然觉得这个世界凸显出一种平时没有的样子：窗子外边不远处是喧闹的烟火和人群，繁华而绚烂，隔着窗子我能感受到热闹的气氛，却听不到任何声音，只能看到这一片繁华。我打开电台，正好在放陈奕迅的圣诞结，很应景。说不上什么感觉，只觉得有些相同的感觉。有时候世界太喧闹的时候，却不想被人群淹没，反而享受这一份别样的孤寂。别人可能会觉得这是孤独，是不好的感受，但是我却觉得并不是如此，热闹近在咫尺，我大可走出房子去一起欢乐，但是我却极其享受这一份难得的自我空间。有谁说过，这个世界总有一半人不理解另一半人的快乐，向来如此。大概就是这个意思吧。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;后来的很多个圣诞节，我再也没有这样的感受了，可能是心境不一样了，即使刻意避开人群和邀请，一个人呆在屋子里，感受到的往往差那么一点火候。有些感觉，真真是可遇而不可求的。还好有这么当时一首歌，记录下我愉快而珍贵的独处情绪。有时候，即使是走在闹市中，突然有这首歌飘到我的耳朵里，我都会觉得整个记忆的神经被激活了，顿时嘴角不自觉上扬，只有我懂的快乐。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我想说，有时候记忆就是这么神奇的存在，我有很多的记忆都是这样依附在音乐上，开心，伤心，失望，惊喜，很多很多的情绪，像小蜜蜂住在蜂巢里一样，住在不同的歌曲里。甚至，有时候不仅仅是歌曲，还是电影，是味道，是颜色，也是触感。</span>','我是好人',26,'1523.jpg',0,'','2013-12-04 11:20:41',NULL,0),(1524,'反正自作多情 是我看家本领','<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">开始明白过来，如此沉迷Tizzy Bac，是因为他们童话般华丽绚烂的外表下，包裹着一颗疲惫受累的心，而钢琴和电子乐的混搭又刚刚好的呐喊出来了那种张力。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　 他们走过很多，经历很多，也确实感受到了世事作用在他们心底的深深刻痕，所以他们选择说出来，不加晦涩不用隐喻又或者是假装一个牢骚系的冗长调调，只是为了发泄出来，在</span>音乐<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">里。而生活中，惠婷说，穿梭这世界，优雅姿态是必要的。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　 就像一方面在沉重的现实里过活，另一方面心里却是个仰赖阳光和温情怀抱的小孩赖在爱丽丝梦游仙境里不肯长大。所以即使是释放也表达的温和旖旎，而用钢琴做主打来描绘这种发泄的顺畅与绚丽多姿，是恰恰合适的。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　 正时这种力量瞬间袭击了我。甚至是在我还没有意识到的一个洗洗漱漱的琐碎早晨，电脑音响里第一次放出节奏轻快</span>风格<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">繁复的维克多的玫瑰那一刻起。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　 世事流转，而我们，要选择一个风轻云淡的态度。这是我在他们超魔幻现实主义的间奏里听到的。单是曲风就足以让我沉陷，更不必说言此意彼的油画套封和看似毫不相关的专辑名</span>歌曲<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">名了。词在Tizzy Bac曲风里是很重要的一个部分。或狂野激勇或知性通达，节奏感强烈，非常精彩。就像你所看到的，我们的惠婷有时会把琐碎的细节藏起来，摆上桌面的往往是自我勉励，陈述内心伤痛或者是温柔的光明向前看，让人会心，句句都那么有道理。总是第二人称的叙述，让人感觉很贴近，她叙述的那个人仿佛就在眼前，一个故事一首歌。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　 我能听懂，因为这就是我现在的生活。&nbsp;</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　 疼痛，倦怠，迷茫，绝望，以一个自以为优雅的姿态。</span>','我是好人',26,'1524.jpg',1,'','2013-12-04 11:23:45',NULL,0),(1525,'自制暖彻心扉的潮流美味','<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">味噌的做法有很多，被大家耳熟能详的是味噌汤，我家每顿饭都会配上一碗。传统味噌汤的做法是，把泡发好的海带、鲣鱼薄片与清水同煮成高汤，再在煮好的高汤里放入味噌，最后加洋葱片、豆腐块、葱即可。浓郁的味噌酱香结合海鲜的</span>美味<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">，很下饭。其它有名的味噌</span>美食<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">，还有味噌萨摩锅：土锅中加水、泡发好的海带、料酒、带骨鸡肉一同熬煮30分钟后，放入芋头、魔芋、白萝卜、胡萝卜、牛蒡、蘑菇、芹菜继续煮至食材软糯，以7：2：1的比例调入味噌、料酒和酱油，待味道浓厚后关火，撒上葱花。带骨的鸡块儿在汤里十分鲜嫩。其它的，还有味噌酱炸猪排、牛肉饼味噌奶汁烤菜、味噌汤面……</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　今天带来在家自制的、吃起来很暖和的味噌美食——味噌牛肚火锅，不仅可以吃到酱香微辣、久煮不烂的牛肚，而且还可以一边吃菜一边喝汤，瞬间就能体验到暖彻心扉的感觉。汤底是由鲣鱼和鸡汤一起熬成的海鲜高汤，再溶入味噌调味。火锅开煮后，还加入了蒜片、辣椒段。整个火锅吃起来，牛肚那是爽脆而回味无穷、韭菜香辣而入口鲜嫩，海鲜高汤的香气更是久久飘在舌尖。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　【味噌牛肚火锅】的做法</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　难易程度：简单</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　准备时间：15分钟</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　制作时间：40分钟</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　分享人数：3人份</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　火锅汤底材料：味噌40克，料酒10克，酱油20克，鸡汤500克，鲣鱼片或鲣鱼素(如果没有，可用少许鸡精代替)10克，干辣椒15支，大蒜半头，香油30克，盐少许(拌香油、蒜末用)。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　食材：牛肚300克，韭菜200克，绿豆芽50克，蘑菇200克，洋白菜400克，豆腐200克，鸡滑(可选)200克。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　【火锅汤底】做法</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　1. 准备鸡汤和各种调料。这个火锅汤底以味噌为主，所以酱油、料酒只需要准备一点就可以了。如果没有鲣鱼片或鲣鱼素，也可以用鸡精代替，起到调味的作用就可以了。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　2.煮锅开放入鸡汤，烧开后，加入鲣鱼素，用筷子搅拌使其在鸡汤溶解中，并继续熬煮10分钟。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　3.在汤里加入料酒，用筷子搅拌均匀，盖上锅盖，接着熬煮5分钟。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　4.待海鲜的味道出来后，掀开锅盖发现，加入酱油，汤底的颜色会明显变深，继续熬煮5分钟。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　5.在汤里加入味噌，一边熬煮一边用勺子搅拌高汤，直到汤里看不到成团的味噌酱时，盖上锅盖，熬煮10分钟。10分钟后关火，盛出备用。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　6.熬好的味噌汤底在放凉后会出现味噌的沉淀，所以汤的表面清澈而颜色浅、底面颜色浓厚而颜色深。使用前，用勺子稍微搅拌几下就可以是其均匀了。</span>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img src=\"http://img1.manshijian.com/upload/member/image/1404/30b8f53dda4e6b325d13874b73bc6144.jpg\" />\r\n</p>\r\n<div class=\"clear\" style=\"padding:0px;margin:0px;border:0px;font-size:1px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;background-color:#FFFFFF;\">\r\n</div>\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　【火锅食材】做法</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　7.干辣椒在热水里泡软，用剪子剪成宽半厘米的小段，备用。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　8.大蒜分成两份，一分切成薄片留用，另一份切成碎末后与盐、香油一起搅拌均匀，作为火锅蘸料。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　9.找一个大点的容器，在水里仔细的清洗牛肚。清洗干净后将牛肚切成2-3厘米长的段。去掉水分，备用。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　10.绿豆芽、韭菜和蘑菇清洗干净。韭菜切成8-10厘米长的段，蘑菇用手掰成一朵一朵的。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　11.洋白菜洗净后切成较大的方片，长、宽越6-7厘米，分开梗和叶，梗的部分将铺在锅底。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　12.豆腐切成长5厘米、宽3厘米、厚度为2厘米的块儿。放在一边切水。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　13.开始将食材摆入火锅。最底下一层铺满洋白菜的梗，上面依次放上牛肚、豆芽、蘑菇和豆腐。因为牛肚块儿小，所以放在锅里靠外的位置，容易夹起。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　14.在锅里正中间的位置，继续放上一列韭菜，上面撒蒜片和辣椒段。食材就摆放完毕了。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　15.在食材上浇满步骤1-6中熬成的味噌海鲜高汤。准备吃饭时，打开开关、煮沸后即可食用。吃完这些蔬菜和牛肚，还可以在准备一些涮火锅的东西，如各种蔬菜、鸡滑等。</span>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img src=\"http://img1.manshijian.com/upload/member/image/1404/986e573c22669bc34b9672ebcd260e02.jpg\" />\r\n</p>\r\n<div class=\"clear\" style=\"padding:0px;margin:0px;border:0px;font-size:1px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;background-color:#FFFFFF;\">\r\n</div>\r\n<br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　小贴士：</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　1.熬煮味噌海鲜汤底时，味噌要在放入鲣鱼素、料酒和酱油之后放进汤里。因为味噌经过长时间的发酵与熟成，本身的味道已经很浓郁了。所以此时熬汤的重点是用鲣鱼素把鸡汤的鲜美味道吊出来，时间需要花得久一点。等味道都出来了，再放入味噌熬煮10分钟就可以了。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　2.味噌熬成的汤在放凉后，酱本身会沉底。所以吃火锅往食材上浇汤底前，要先用勺子在汤里搅拌几下，使汤质地均匀。之后，再向锅里倒入这个汤底，煮开后就可以吃了。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　3.这种火锅是先吃完锅里的牛肚、韭菜和洋白菜，再继续涮其他材料。所以在家里吃时，可以准备其他自己爱吃的蔬菜、鸡滑等。搭配味噌海鲜汤底也是非常鲜美的。</span>\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img src=\"http://img1.manshijian.com/upload/member/image/1404/c770ff9a73f87fa250680a326837db5f.jpg\" />\r\n</p>','我是好人',25,'1525.jpg',1,'','2013-12-04 11:26:14',NULL,0),(1526,'烤箱版什锦鸡肉串','<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">大拍档最红火的可能就是烧烤摊了，烤肉串虽然味美，但烟熏火燎的气味实在让人难以忍受，卫生质量也着实令人担心。其实可以在家动动手，准备好各种食材，蔬菜、肉肉、水果任何搭配，用烤箱加热几分钟，</span>美味<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">又</span>健康<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">的烤串就呈现在眼前了!最重要的是无油烟，不会增加PM2.5值，没给祖国的空气质量下降而添乱!</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　原料：鸡胸肉300克、口蘑2个、洋葱1个、彩椒2个、圣女果适量</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　调料：料酒10ml、生抽15ml、白糖5克、盐、干淀粉、黑胡椒、辣椒粉、橄榄油</span><br />\r\n<p align=\"center\" style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<img src=\"http://img1.manshijian.com/upload/member/image/1391/2e4cb3d54bfe6282557ef846c7f8dd66.png\" />\r\n</p>\r\n<div class=\"clear\" style=\"padding:0px;margin:0px;border:0px;font-size:1px;color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;background-color:#FFFFFF;\">\r\n</div>\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　做法：</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　1、把鸡胸肉切成2cm见方的块儿，用料酒、生抽、白糖、淀粉抓匀，腌2小时左右。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　2、把口蘑、洋葱、彩椒等切成小方块，和腌好的鸡肉块一起串起来。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　3、用刷子在上面刷上一层橄榄油，再均匀地洒上盐、黑胡椒碎、辣椒粉等调料，放在200度的烤箱里烤7、8分钟。</span><br />\r\n<span style=\"color:#666666;font-family:微软雅黑, Verdana, Geneva, sans-serif;font-size:14px;line-height:30px;background-color:#FFFFFF;\">　　4、烤到4分钟时打开烤箱把肉串翻一次面，等听到烤箱“叮”的一声，打开门取出来吧，香喷喷的鸡肉串已经烤好了!是不是比烧烤摊上的更美味、更漂亮、更健康呢?</span>','我是好人',25,'1526.jpg',0,'','2013-12-04 11:26:59',NULL,0),(2386,'','','',0,NULL,0,NULL,'2015-06-27 10:18:26',NULL,0);

