from django import formsclass ContactForm(forms.Form):	name 	= forms.CharField(min_length=5)	address = forms.CharField() 	tel     = forms.CharField()	message	= forms.CharField(widget=forms.Textarea)		def clean_message(self):			message = self.cleaned_data['message']			num_words = len(message.split())			if num_words < 4:				raise forms.ValidationError("Not enough words!")			return message